('nuaddattribute', 'launch', 'nuaddattribute', 'Add attribute', NULL, NULL, '', '', '', 0, 0, '0', NULL, 'SELECT * FROM Launch Form 0', '/*\n    0|autocomplete|\n    1|accesskey|\n    2|data-|\n    3|maxlength|\n    4|placeholder|\n    5|spellcheck|\n    6|title|\n    7|value|\n    8|nu-label-position\n*/\n\nnuHide(\'plh_value_select\');\nnuHide(\'plh_attribute_name\');\nnuHideTabs(0);\nnuSelectRemoveEmpty(\'plh_attribute\', \'-1\');\nnuAddActionButton(\'nuAddAttribute\', \'Add\', \'addAttribute();\');\n\nfunction nuOnMobileViewComplete() { debugger;\n  const currentTop = $(\'#plh_value_text\').css(\'top\');\n  const currentTopLabel = $(\'#label_plh_value_text\').css(\'top\');\n  $(\'#plh_value_select\').css(\'top\', currentTop);\n  $(\'#label_plh_value_select\').css(\'top\',  currentTopLabel);\n}\n\nif (typeObj().val() !== \'input\') {\n    nuSelectRemoveOption(\'plh_attribute\', \'7\').val(\'\');\n}\n\nfunction onAttributeChanged(value) {\n\n    nuShow(\'plh_value_select\', value === \'0\' || value === \'5\' || value === \'8\');\n    nuShow(\'plh_value_text\', !(value === \'0\' || value === \'5\' || value === \'8\'));\n    nuShow(\'plh_attribute_name\', value === \'2\');\n    \n    nuSetValue(\'plh_value_text\',\'\');\n\n    if (value === \'0\') {\n        $(\"#plh_value_select\").empty();\n        addSelectOption(\'0\', \'on\');\n        addSelectOption(\'1\', \'off\');\n    } else if (value === \'5\') {\n        $(\"#plh_value_select\").empty();\n        addSelectOption(\'0\', \'true\');\n        addSelectOption(\'1\', \'false\');\n    } else if (value === \'4\') {\n        nuSetPlaceholder(\'plh_value_text\', \'Placeholder text\');\n    } else if (value === \'6\') {\n        nuSetPlaceholder(\'plh_value_text\', \'Tooltip text\');\n    } else if (value === \'2\') {\n        nuSetPlaceholder(\'plh_value_text\', \'String value\');\n        $(\'#plh_attribute_name\').trigger(\"focus\");\n    } else if (value === \'1\') {\n        nuSetPlaceholder(\'plh_value_text\', \'Single character\');\n    } else if (value === \'3\') {\n        nuSetPlaceholder(\'plh_value_text\', \'Number value\');\n    }  else if (value === \'7\') {\n        nuSetPlaceholder(\'plh_value_text\', \'String\');\n    }  else if (value === \'8\') {\n        $(\"#plh_value_select\").empty();\n        addSelectOption(\'top\', \'top\');\n    } \n\n}\n\nfunction addSelectOption(value, text) {\n    $(\'#plh_value_select\').append(\'<option value=\"\' + value + \'\">\' + text + \'</option>\');\n}\n\nfunction attributesObj() {\n    return $(\"#sob_input_attribute\", window.parent.document);\n}\n\nfunction typeObj() {\n    return $(\"#sob_all_type\", window.parent.document);\n}\n\n\nfunction valueObjText() {\n    return $(\'#plh_value_text\').is(\":visible\") ? nuGetValue(\'plh_value_text\') : nuGetValue(\'plh_value_select\', \'text\');\n}\n\nfunction addAttribute() {\n    \n    let v = valueObjText();\n    let an = nuGetValue(\'plh_attribute_name\');\n\n    if (v === \'\') {\n        nuMessage(`${nuTranslate(\'Error\')}`, `${nuTranslate(\"Missing value\")}`);\n        return;\n    }\n\n    if (an === \'\' && nuGetValue(\'plh_attribute\') == \'2\') { // data-\n        nuMessage(`${nuTranslate(\'Error\')}`, `${nuTranslate(\"Missing name\")}`);\n        return;\n    }    \n\n    let t = attributesObj();\n    let k = nuGetValue(\'plh_attribute\', \'text\') + an;\n    let kEquals = k + \'=\"\';\n\n    if (t.val().includes(kEquals)) {\n        nuMessage(`${nuTranslate(\'Error\')}`, `${nuTranslate(\"The attribute already exists\")}`);\n        return;\n    }\n\n    let kv = kEquals + v + \'\"\';\n\n    if (t.val() === \'\') {\n        t.val(kv).change();\n    } else {\n        t.val(t.val() + \',\' + kv).change();\n    }\n\n}', NULL, NULL, NULL, '1', NULL),
('nuadddbcolumn', 'launch', 'nuadddbcolumn', 'Add Database Column', NULL, NULL, '', '', '', 0, 0, '0', NULL, '', 'function nuAddDBColumnGetDataType(t, i, selectMultiple) {\n\n    let dt = \'VARCHAR(100)\';\n\n    if (t == \'lookup\') {\n        dt = \'VARCHAR(25)\';\n    }\n    if (t == \'select\' && ! selectMultiple) {\n        dt = \'VARCHAR(100)\';\n    }\n    if (t == \'select\' && selectMultiple) {\n        dt = \'VARCHAR(1000)\';\n    }\n    if (t == \'calc\') {\n        dt = \'DECIMAL(12,4)\';\n    }\n    if (t == \'textarea\') {\n        dt = \'TEXT\';\n    }\n\n    if (t == \'input\') {\n        let dtInput = \'\';\n        if (i == \'date\' || i == \'nuDate\') {\n            dtInput = \'DATE\';\n        }\n        if (i == \'number\') {\n            dtInput = \'INT\';\n        }\n        if (i == \'nuAutoNumber\') {\n            dtInput = \'BIGINT UNSIGNED\';\n        }\n        if (i == \'nuNumber\') {\n            dtInput = \'DECIMAL(12,4)\';\n        }\n        if (i == \'file\') {\n            dtInput = \'LONGTEXT\';\n        }\n        dt = dtInput != \'\' ? dtInput: \'VARCHAR(100)\';\n\n    }\n\n    return dt;\n}\n\n\nvar table = parent.$(\'#sob_all_table\').val();\nvar id = parent.$(\'#sob_all_id\').val();\nvar type = parent.$(\'#sob_all_type\').val();\nvar input = parent.$(\'#sob_input_type\').val();\nvar selectMultiple = parent.nuGetValue(\'sob_select_multiple\')\nvar dataType = nuAddDBColumnGetDataType(type, input);\n\nvar qry = \'`$column` $type NULL DEFAULT NULL\';\nqry = qry.replace(\'$column\', id);\nqry = qry.replace(\'$type\', dataType);\n\nvar start = \'ALTER TABLE `$table` ADD\'\nstart = start.replace(\'$table\', table);\n\n$(\'#sql_query_word\').html(start);\n$(\'#sql_query\').val(qry);\n\nnuAddActionButton(\'Run\', \'Run\', \'nuHasNotBeenEdited(); nuRunPHPHidden(\"NURUNADDDBCOLUMN\")\');\n\n$(\'#sql_query\').addClass(\'sql\');\n$(\'.sql\').on(\'dblclick\', function() {\n    nuOpenAce(\'SQL\', this.id);\n});\n\nnuSetProperty(\'sob_all_table\', table);\nnuRefreshSelectObject(\'sql_after_column\');', NULL, NULL, NULL, '1', NULL),
('nuauthentication', 'launch', 'nuauthentication', 'Authentication', NULL, NULL, '', '', '', 0, 0, '0', NULL, '', 'function nuGet2FAProcedure() {\n    const d = nuDevMode();\n    const p = \'nuAuthentication2FA\';\n    return d ? p + \'_Template\': p;\n}\n\nfunction nu2FAVerify() {\n    nuSetProperty(\'auth_code_verify\', $(\'#auth_code\').val());\n    nuSetProperty(\"nuauthcommand\", \"verify\");\n    const p = nuGet2FAProcedure();\n    nuRunPHPHidden(p);\n}\n\nfunction nu2FASendToken() {\n    nuSetProperty(\"nuauthcommand\", \"send\");\n    const p = nuGet2FAProcedure();\n    nuRunPHPHidden(p);\n}\n\nfunction handleEnterKey() {\n\n    $(\'#auth_code\').on(\'keydown\', function(evt) {\n        if (evt.key === \'Enter\') {\n            evt.preventDefault();\n            nu2FAVerify();\n        }\n    });\n\n}\n\nhandleEnterKey();\nnuHideHolders(0, 2);\n\n\n\n// Prevent [DOM] Password field is not contained in a form:\n$(\"#auth_code_verify\").wrap(\"<form id=\'nuFromVerif\' action=\'#\' method=\'post\' onsubmit=\'return false\'>\");', NULL, NULL, NULL, '1', NULL),
