L.buildSQL();\r\n}\r\n\r\nfunction nuSelectSetSFCB() {\r\n\r\n $(\'.nuSubformCheckbox.zzzzsys_select_clause\').on(\'click\',\r\n function() {\r\n nuSelectSFCB();\r\n });\r\n\r\n\r\n}\r\n\r\nfunction nuSelectResizeSQL() {\r\n\r\n if ($(\'#sqlframe\').css(\'height\') == \'460px\') {\r\n $(\'#sqlframe\').css(\'height\', 700);\r\n } else {\r\n $(\'#sqlframe\').css(\'height\', 460);\r\n }\r\n\r\n}', NULL, '1', NULL),
('nusession', 'browse', 'nusession', 'Sessions', NULL, NULL, 'zzzzsys_session', 'zzzzsys_session_id', '', 0, 0, '0', NULL, 'SELECT \n   *\nFROM (\n    SELECT \n        zzzzsys_session_id,\n        TRIM(BOTH \'\"\' FROM JSON_EXTRACT(sss_access, \'$.session.zzzzsys_user_id\')) AS login,\n        NULL AS user,\n        sss_login_time AS login_time,\n        TRIM(BOTH \'\"\' FROM JSON_EXTRACT(sss_access, \'$.session.ip_address\')) AS ip\n    FROM \n        zzzzsys_session\n    WHERE \n        TRIM(BOTH \'\"\' FROM JSON_EXTRACT(sss_access, \'$.session.global_access\')) = \'1\'\n    \n    UNION ALL\n    \n    SELECT \n        zzzzsys_session_id,\n        sus_login_name AS login,\n        sus_name AS user,\n        sss_login_time AS login_time,\n        TRIM(BOTH \'\"\' FROM JSON_EXTRACT(sss_access, \'$.session.ip_address\')) AS ip\n    FROM \n        zzzzsys_user\n        JOIN zzzzsys_session ON zzzzsys_user_id = TRIM(BOTH \'\"\' FROM JSON_EXTRACT(sss_access, \'$.session.zzzzsys_user_id\'))\n    WHERE \n        TRIM(BOTH \'\"\' FROM JSON_EXTRACT(sss_access, \'$.session.global_access\')) = \'0\'\n) AS combined_sessions\nORDER BY \n    login_time DESC\n', 'nuHide(\'nuAddButton\');\n\n', NULL, NULL, NULL, NULL, NULL);
