('nu5fee06b1acbe4bb', 'NUBACKUP', 'Create a Database Backup using mysqldump', 'nubuilder', 'if (nuDemo()) return;\n\n// *******************************************************************\n\n// Settings:\n\n// Path where the mysqldump library will save the backup file. Modify this path if needed.\n$PATH_MYSQLDUMP = __DIR__ . \'/libs/mysqldump/dumps/\';\n\n// Filename of the backup file, using the current date and time with a unique identifier and the name of the database.\n$FILE_NAME = date(\'m-d-Y_H:i:s\') . \'_\' . uniqid() . \'_\' . \'nuBuilder_backup\' . \'.sql.gzip\';\n\n// Settings for the mysqldump library. Modify these if needed.\n$dumpSettings = [];\n$dumpSettings[\'single-transaction\'] = false;\n$dumpSettings[\'no-create-info\'] = false;\n$dumpSettings[\'lock-tables\'] = false;\n$dumpSettings[\'add-locks\'] = false;\n$dumpSettings[\'extended-insert\'] = false;\n$dumpSettings[\'skip-definer\'] = true;\n$dumpSettings[\'routines\'] = true;\n$dumpSettings[\'compress\'] = \'Gzip\'; \n\n\n// *******************************************************************\n\n\n// Path to Mysqldump.php\n$path_mysqldump_php = __DIR__ . \'/libs/mysqldump/Mysqldump.php\';\n\ntry {\n    require_once ($path_mysqldump_php);\n} catch (Exception $e) {\n    nuDisplayError(\'require_once failed! Error: \'.$e);\n}\n\n/*\n// DEV\n    includeTables($dumpSettings);\n    excludeTables($dumpSettings);\n*/\n\n// Create Mysqldump\nglobal $nuConfigDBHost, $nuConfigDBName, $nuConfigDBUser, $nuConfigDBPassword, $nuConfigDBPort;\n$nuConfigDBPort = $nuConfigDBPort ?? \'3306\';\n\n$dumper = new Ifsnop\\Mysqldump\\Mysqldump(\"mysql:host=$nuConfigDBHost;dbname=$nuConfigDBName;port=$nuConfigDBPort\", $nuConfigDBUser, $nuConfigDBPassword, $dumpSettings);\n\n$tableWheres = [\n    \"zzzzsys_session\" => \"zzzzsys_session_id not like \'s%\' \"\n];\n\n// Exclude nu records\nif (\"#nubackup_user_records_only#\" == \"1\") {\n\n    $tableWheresNotNu = [\n        \"zzzzsys_access\" => tableIdNotLikeNu(\"zzzzsys_access\"),\n        \"zzzzsys_access_form\" => tableIdNotLikeNu(\"zzzzsys_access_form\"),\n        \"zzzzsys_access_php\" => tableIdNotLikeNu(\"zzzzsys_access_php\"),\n        \"zzzzsys_access_report\" => tableIdNotLikeNu(\"zzzzsys_access_report\"),\n        \"zzzzsys_browse\" => tableIdNotLikeNu(\"zzzzsys_browse\"),\n        \"zzzzsys_cloner\" => tableIdNotLikeNu(\"zzzzsys_cloner\"),\n        \"zzzzsys_code_snippet\" => tableIdNotLikeNu(\"zzzzsys_code_snippet\"),\n        \"zzzzsys_debug\" => tableIdNotLikeNu(\"zzzzsys_debug\"),\n        \"zzzzsys_event\" => tableIdNotLikeNu(\"zzzzsys_event\"),\n        \"zzzzsys_file\" => tableIdNotLikeNu(\"zzzzsys_file\"),\n        \"zzzzsys_form\" => tableIdNotLikeNu(\"zzzzsys_form\"),\n        \"zzzzsys_format\" => tableIdNotLikeNu(\"zzzzsys_format\"),\n        \"zzzzsys_info\" => tableIdNotLikeNu(\"zzzzsys_info\"),\n        \"zzzzsys_note\" => tableIdNotLikeNu(\"zzzzsys_note\"),\n        \"zzzzsys_note_category\" => tableIdNotLikeNu(\"zzzzsys_note_category\"),\n        \"zzzzsys_object\" => tableIdNotLikeNu(\"zzzzsys_object\"),\n        \"zzzzsys_php\" => tableIdNotLikeNu(\"zzzzsys_php\"),\n        \"zzzzsys_report\" => tableIdNotLikeNu(\"zzzzsys_report\"),\n        \"zzzzsys_select\" => tableIdNotLikeNu(\"zzzzsys_select\"),\n        \"zzzzsys_select_clause\" => tableIdNotLikeNu(\"zzzzsys_select_clause\"),\n        \"zzzzsys_setup\" => tableIdNotLikeNu(\"zzzzsys_setup\"),\n        \"zzzzsys_tab\" => tableIdNotLikeNu(\"zzzzsys_tab\"),\n        \"zzzzsys_timezone\" => tableIdNotLikeNu(\"zzzzsys_timezone\"),\n        \"zzzzsys_translate\" => tableIdNotLikeNu(\"zzzzsys_translate\"),\n        \"zzzzsys_user\" => tableIdNotLikeNu(\"zzzzsys_user\")\n    ];\n\n\n    $tableWheres = array_merge($tableWheresNotNu, $tableWheres);\n\n}\n\n\n$downloadFile = true;\n$dumper->setTableWheres($tableWheres);\n\n$dump_file = $PATH_MYSQLDUMP . nuSanitizeFilename($FILE_NAME);\n\n// Start the dump\ntry {\n\n    if (!is_dir($PATH_MYSQLDUMP)) {\n        mkdir($PATH_MYSQLDUMP, 0755);\n    }\n\n    $dumper->start($dump_file);\n\n    /*\n        // DEV\n        if ($downloadFile) {\n            header(\'Content-Type: text/plain;charset=utf-8\');\n            header(\"Pragma: no-cache\");\n            header(\"Expires: 0\");\n            header(\'Content-Disposition: attachment; filename=\"dump.sql\"\');\n            $dumper->start(\'php://output\');\n        } else {\n            $dumper->start($dump_file);\n        }\n    */\n}\ncatch(\\Exception $e) {\n    nuDisplayError(\'Export Error: \' . $e->getMessage());\n}\n\n$dump_file = base64_encode($PATH_MYSQLDUMP . $FILE_NAME);\n\n$js = \"\n   nuMessage([\'<h2>Export completed!</h2><br>SQL Dump saved in \' + atob(\'$dump_file\')]);\n\";\n\nnuJavaScriptCallback($js);\n\nfunction tableIdNotLikeNu($tableName) {\n    return \"$tableName\" . \"_id not like \'nu%\'\";\n}\n\nfunction includeTables(array & $dumpSettings) {\n\n    $tables = nuGetProperty(\'nubackup_tables_include\');\n    if ($tables) {\n        $dumpSettings[\"include-tables\"] = json_decode(stripslashes($tables));\n    }\n\n}\n\nfunction excludeTables(array & $dumpSettings) {\n\n    $tables = nuGetProperty(\'nubackup_tables_exclude\');\n    if ($tables) {\n        $dumpSettings[\"exclude-tables\"] = json_decode(stripslashes($tables));\n    }\n\n}', 'hide', '', '1', '0', '0', '', NULL),
('nubuildtable_BB', 'nubuildtable_BB', 'System PHP', 'nubuilder', '$select = \"\n    SELECT\n     CONCAT(\n     \'PROCEDURE:\',\n     `zzzzsys_php`.`zzzzsys_php_id`\n     ) AS `zzzzsys_php_id`,\n     `zzzzsys_php`.`sph_code` ,\n     `zzzzsys_php`.`sph_description`\n    FROM\n     `zzzzsys_php`\n    WHERE\n     (\n     (`zzzzsys_php`.`sph_system` <> \'1\') \n     AND (LOCATE(\'{TABLE_ID}\', `zzzzsys_php`.`sph_php`) > \'0\') AND (`zzzzsys_php`.`sph_template` <> 1)\n     )\n     \n    UNION\n    \n    SELECT\n     CONCAT(\n     \'SQL:\',\n     `zzzzsys_select`.`zzzzsys_select_id`\n     ) AS `id`,\n     \'nuSQL\',\n     `zzzzsys_select`.`sse_description`\n    FROM\n     `zzzzsys_select`\n    WHERE\n     (\n     (\n     `zzzzsys_select`.`sse_system` IS NULL\n     ) OR(`zzzzsys_select`.`sse_system` = \'\')\n     )\n     \n    UNION\n    \n    SELECT\n     CONCAT(\n     \'TABLE:\',\n     `TABLE_NAME`\n     ) AS `id`,\n     \'nuTABLE\',\n     `TABLE_NAME` \n    FROM \n     `information_schema`.`tables` \n    WHERE \n     `TABLE_SCHEMA` = database()\n     AND `TABLE_NAME` NOT LIKE \'\\\\\\\\_%\' ESCAPE \'\\\\\\\\\'\n\";\n\n\n$select = str_replace(\"{TABLE_ID}\", \'#\'.\'TABLE_ID\'.\'#\', $select);\n\n$insert = \"\nINSERT INTO zzzzsys_report_data ( \n `zzzzsys_report_data_id`,\n `srd_code`,\n `srd_description`)\n\".$select;\n\n\nnuRunQuery(\'DELETE FROM `zzzzsys_report_data`\');\nnuRunQuery($insert);\n', NULL, NULL, '1', '0', '0', NULL, NULL),
('nu5ff7efb1ed369a6', 'NUREFRESHSELECTOBJECT', 'Refresh a select object', 'nubuilder', 'function nuGetSelectValues($formId, $selectId) {\n\n    $sql = \"\n        SELECT\n            sob_select_sql\n        FROM\n            `zzzzsys_object`\n        WHERE\n            sob_all_zzzzsys_form_id = ? AND sob_all_id = ?\n    \";\n\n    $t = nuRunQuery($sql, [$formId, $selectId]);\n\n    $a = [];\n    if (db_num_rows($t) == 1) {\n\n        $r = db_fetch_row($t);\n        if ($r != false) {\n            \n            $disS = nuReplaceHashVariables($r[0]);\n\n            $t = nuRunQuery($disS);\n\n            while ($row = db_fetch_row($t)) {\n                $a[] = $row;\n            }\n\n            return json_encode($a);\n        }\n\n    }\n\n    return false;\n\n}\n\nfunction nuPopulateSelectObject($formId, $selectId, $removeBlank, $prefix) {\n\n    $j = nuGetSelectValues($formId, $selectId);\n\n    $selectId = $prefix.$selectId;\n\n    $cb = \"if (window.nuSelectObjectRefreshed) {\n    	    nuSelectObjectRefreshed(\'$formId\', \'$selectId\', nuSelectObjectRefreshedCount);\n         }\";\n\n    if ($j == false) {\n        \n        return \"var count = -1; \".$cb;\n        \n    } else {\n    	\n    return \"\n    	function nuPopulateSelectObject() {\n    	\n    		var p = $j;\n    \n    		$(\'#$selectId\').empty();\n    		\n    		if (\'$removeBlank\' == \'0\' ) {\n    		    $(\'#$selectId\').append(\'<option value=\\\"\\\"></option>\');\n    		}\n            \n            var count = 0;\n    \n    		if (p != \'\') {\n    		    var s = nuIsSaved();\n    			\n    			for (var i = 0; i < p.length; i++) {\n    				$(\'#$selectId\').append(\'<option value=\\\"\' + p[i][0] + \'\\\">\' + p[i][1] + \'</option>\');\n    				count ++;\n    			}\n    			\n    			if (s) { nuHasNotBeenEdited(); }\n    			\n    		}\n    		\n    		return count;\n    	}\n    	\n    	var nuSelectObjectRefreshedCount = nuPopulateSelectObject();\n\n    \".$cb;\n    }\n\n}\n\nfunction nuRefreshSelectObject($selectId, $formIdHk, $removeBlank, $prefixHK) {\n\n    if (!nuHasProperty($formIdHk, $formId, false)) {\n        $formId = \'#form_id#\';\n    }\n\n    if (!nuHasProperty($prefixHK, $prefix, false)) {\n        $prefix = \'\';\n    }\n\n    $js = nuPopulateSelectObject($formId, $selectId, $removeBlank, $prefix);\n    nuJavaScriptCallback($js);\n\n}\n\nnuRefreshSelectObject(\'#NUREFRESHSELECTOBJECT_selectid#\', \'NUREFRESHSELECTOBJECT_formid\', \'#NUREFRESHSELECTOBJECT_removeblank#\',\'NUREFRESHSELECTOBJECT_prefix\');', 'hide', '', '1', '1', '0', '', NULL),
('nu5ff8249659aef00', 'NUREFRESHDISPLAYOBJECT', 'Refresh a display object', 'nubuilder', 'function nuGetDisplayValue($formId, $id) {\n	\n    $sql = \"SELECT sob_display_sql, sob_display_procedure FROM `zzzzsys_object` WHERE sob_all_zzzzsys_form_id = ? AND sob_all_id = ?\";\n    $selectObject = nuRunQuery($sql, [$formId, $id]);\n	\n    if (db_num_rows($selectObject) == 1) {\n        $obj = db_fetch_object($selectObject);\n        \n        if ($obj != false) {\n		\n            $displayProcedure = $obj->sob_display_procedure;\n            \n            if (empty($displayProcedure)) {\n              \n                $disS = nuReplaceHashVariables($obj->sob_display_sql);\n                $disT = nuRunQuery($disS);\n\n                if (db_num_rows($disT) >= 1) {\n                    $disR = db_fetch_row($disT);\n                    return $disR[0];\n                } else {\n                    return \"\";\n                }\n				\n            } else {\n\n                $procedureCode = nuProcedure($displayProcedure);\n                if ($procedureCode !== \'\') {\n                    return nuEval($displayProcedure, $procedureCode);\n                } else {\n                    return false;\n                }\n            }\n        }\n    }\n\n    return false;\n}\n\n\nfunction nuRefreshDisplayObject($displayId, $formIdHk, $prefixHK) {\n\n    if (!nuHasProperty($formIdHk, $formId, false)) {\n        $formId = \'#form_id#\';\n    }\n\n    if (!nuHasProperty($prefixHK, $prefix, false)) {\n        $prefix = \'\';\n    }\n\n	$value = nuGetDisplayValue($formId, $displayId);\n	$displayId = $prefix.$displayId;\n		\n	if ($value === false && $value !== \'\') {\n		$js = \"nuMessage(nuTranslate(\'Error\'), nuTranslate(\'Failed to refresh the Display Object:\') + \' $displayId\'); \";\n	} else {\n\n		$js = \" \n		    \n		    function nuRefreshDisplayObjectSetNewValue(displayId, value) {\n    			let obj = $(\'#\' + displayId);\n    			const format = obj.attr(\'data-nu-format\');\n    			const formattedValue = nuFORM.addFormatting(value, format);\n    			nuSetValue(displayId, formattedValue);\n		    }\n\n		    nuRefreshDisplayObjectSetNewValue(\'$displayId\', \'$value\');\n			\n			if (window.nuDisplayObjectRefreshed) {\n				nuDisplayObjectRefreshed(\'$displayId\', \'$formId\');\n			}\n		\";\n	}\n    \n    nuJavaScriptCallback($js);\n\n}\n\nnuRefreshDisplayObject(\'#NUREFRESHDISPLAYOBJECT_displayid#\', \'NUREFRESHDISPLAYOBJECT_formid\',\'NUREFRESHDISPLAYOBJECT_prefix\');', 'hide', '', '1', '1', '0', '', NULL),
('nu6068bee1b6c63be', 'NURUNADDDBCOLUMN', 'Add a new database column', 'nubuilder', 'if (nuHash() [\'GLOBAL_ACCESS\'] == \'0\') return;\n\n$sql = \'ALTER TABLE `#sob_all_table#` ADD \' . \'#sql_query#\';\n$after = \'#sql_after_column#\';\nif ($after != \'\') {\n    $sql = $sql . \" AFTER \" . $after;\n}\n\nif (preg_match(\'[DELETE |DROP |INSERT |;]\', strtoupper($sql))) {\n    $r = - 2;\n}\nelse {\n    $r = nuRunQuery($sql, [] , true);\n}\n\nif ($r == 0) {\n    $js = \"nuMessage(nuTranslate(\'Information\'), nuTranslate(\'The column has been created successfully\')); nuRefreshSelectObject(\'sql_after_column\');\";\n}\nelse if ($r == - 1) {\n    $js = \"nuMessage(nuTranslate(\'Error\'), nuTranslate(\'An error occured while creating the column\'), nuTranslate(\'Check nuDebug Results for details\'));\";\n\n}\nelse if ($r == - 2) {\n    $js = \"nuMessage(nuTranslate(\'Error\'), [nuTranslate(\'An error occured while creating the column\'), nuTranslate(\'The query contains invalid keywords\')]);\";\n\n}\n\nnuJavaScriptCallback($js);\n', 'hide', '', '1', '0', '0', '', NULL),
