('nu610fd4ba4e73ede', 'NUUPDATEOBJECT', 'Update an Object\'s Properties', 'nubuilder', 'if (nuDemo()) return;\n\n$id = \"#NUUPDATEOBJECT_id#\";\n$value = \"#NUUPDATEOBJECT_value#\";\n$formId = \"#NUUPDATEOBJECT_form_id#\";\n$type = \"#NUUPDATEOBJECT_type#\";\n$column = \"#NUUPDATEOBJECT_column#\";\n\nif ($type == \'tab\') {\n\n    $q = \"\n            UPDATE `zzzzsys_tab`\n            SET $column = ?\n            WHERE `zzzzsys_tab_id` = ?\n    \";\n\n    nuRunQuery($q, [$value, $id]);\n\n} else if ($type == \'edit\') {\n\n    $q = \"\n        UPDATE `zzzzsys_object`\n        SET $column = ?\n        WHERE `sob_all_zzzzsys_form_id` = ? AND `sob_all_id` = ?\n    \";\n\n    nuRunQuery($q, [$value, $formId, $id]);\n\n} else {\n\n    $q = \"\n            UPDATE `zzzzsys_browse`\n            SET `$column` = ?\n            WHERE `sbr_zzzzsys_form_id` = ? AND `sbr_order` = ?\n    \";\n\n    nuRunQuery($q, [$value, $formId, $id]);\n\n}', 'hide', NULL, '1', '0', '0', '', NULL),
('nu61d8283e98e8b2e', 'NUCHECKPASSWORDPOLICY_TEMPLATE', 'Rules for enforcing Password policies', 'nubuilder', '\nfunction nuFATick($nok) {\n\n    $icon = $nok == false ? \"far fa-check-circle\" : \"far fa-times-circle\";\n    $color = $nok == false ? \"green\" : \"red\";\n\n    return \"&nbsp;&nbsp;<i class=\'$icon\' style=\'color:$color\'></i>&nbsp;&nbsp;\";\n\n}\n\nfunction nuCheckPasswordPolicy() {\n\n    $oldpw = \'#old_password#\';\n    $newpw = \'#new_password#\';\n\n    $atLeast = nuTranslate(\"Your New Password must contain at least\").\":\";\n    $msg = \'\';\n    $c = 0;\n\n    $check = strlen($newpw) < 8;\n    if ($check == true) $c++;\n    $msg .= nuFATick($check) . nuTranslate(\"8 Characters\") . \"<br>\";\n\n    $check = !preg_match(\"#[0-9]+#\", $newpw);\n    if ($check == true) $c++;\n    $msg .= nuFATick($check) . nuTranslate(\"1 Number\") . \"<br>\";\n\n    $check = !preg_match(\"#[A-Z]+#\", $newpw);\n    if ($check == true) $c++;\n    $msg .= nuFATick($check) . nuTranslate(\"1 Capital Letter\") . \"<br>\";\n\n    $check = !preg_match(\"#[a-z]+#\", $newpw);\n    if ($check == true) $c++;\n    $msg .= nuFATick($check) . nuTranslate(\"1 Lowercase Letter\") . \"<br>\";\n\n    $check = !preg_match(\'/[\\\'\\/~`\\!@#\\$%\\^&\\*\\(\\)_\\-\\+=\\{\\}\\[\\]\\|;:\"\\<\\>,\\.\\?\\\\\\]/\', $newpw);\n    if ($check == true) $c++;\n    $msg .= nuFATick($check) . nuTranslate(\"1 Special Character\") . \"<br>\";\n\n    if ($msg != $atLeast) $msg = $atLeast . \"<br><br>\" . $msg;\n\n    if ($newpw === $oldpw) {\n        $msg .= \"<br>\" . nuFATick(true) . nuTranslate(\"The provided New Password cannot be the same as the Current Password\") . \"<br>\";\n        $c++;\n    }\n\n    if ($c > 0) {\n        nuDisplayError($msg);\n        return false;\n    }\n    else {\n        return true;\n    }\n\n}\n\n$check = nuCheckPasswordPolicy();\n\n', 'hide', '', '0', '1', '1', '', NULL),
('nu61eceada0768c21', 'NUCHANGEPASSWORD', 'Change user password', 'nubuilder', '$oldPassword = \'#old_password#\';\n$newPasswordCheck = \'#new_password_check#\';\n$newPassword = \'#new_password#\';\n$session = \'#SESSION_ID#\';\n$userId = \"#USER_ID#\";\n\n\n\n//--\n\n$s = \"\n    SELECT sss_access\n    FROM zzzzsys_session\n    WHERE zzzzsys_session_id = ?\n\";\n\n$t = nuRunQuery($s, [$session]);\n$r = db_fetch_object($t);\n$j = json_decode($r->sss_access);\n\n//--\n\n$s = \"\n SELECT\n    sus_login_password,\n    sus_change_password\n FROM zzzzsys_user\n WHERE zzzzsys_user_id = ?\n\";\n\n$t = nuRunQuery($s, [$userId]);\n$r = db_fetch_object($t);\n$current = $r->sus_login_password;\n$changePassword = $r->sus_change_password;\n\n$useMd5 = $_SESSION[\'nubuilder_session_data\'][\'USE_MD5_PASSWORD_HASH\'] == true;\n\nif ($useMd5) {\n    $check = md5($oldPassword) == $current;\n} else {\n    $check = password_verify($oldPassword, $current);\n}\n\nif ($check == false) {\n    nuDisplayError(\'<h2>\' . nuTranslate(\'Error\') . \'</h2>\' . nuTranslate(\'Incorrect Password\'));\n    return;\n}\n\nif ($oldPassword == $newPassword) {\n\n    nuDisplayError(\'<h2>\' . nuTranslate(\'Error\') . \'</h2>\' . nuTranslate(\'Your new password cannot be the same as your old password\'));\n    return;\n}\n\nif ($oldPassword === \'\' || $newPassword === \'\') {\n    nuDisplayError(\'<h2>\' . nuTranslate(\'Error\') . \'</h2>\' . nuTranslate(\'The password cannot be left blank\'));\n    return;\n}\n\n\n//--\n\n$p = nuProcedure(\'nuCheckPasswordPolicy\');\n\nif ($p != \'\') {\n\n    eval($p);\n\n    if ($check == false) {\n        return;\n    }\n\n}\n\n//--\n\nif ($newPassword == $newPasswordCheck) {\n\n    $s = \"\n\n         UPDATE zzzzsys_user\n         SET\n            sus_login_password = ?,\n            sus_change_password = ?\n         WHERE zzzzsys_user_id = ?\n    \";\n\n    $pwHash = $useMd5 == true ? md5($newPassword) : nuPasswordHash($newPassword);\n\n    nuRunQuery($s, [$pwHash, \'0\', $userId]);\n\n    nuSetUserJSONData(\'PASSWORD_CHANGED_TIME\', time(), $userId);\n    nuSetUserJSONData(\'PASSWORD_CHANGED_SOURCE\', \'user\', $userId);\n\n\n\n    if (nuObjKey($_SESSION[\'nubuilder_session_data\'], \'SESSION_CHANGE_PW_STATUS\') == \'PENDING\') {\n        $_SESSION[\'nubuilder_session_data\'][\'SESSION_CHANGE_PW_STATUS\'] = \'CHANGED\';\n    }\n\n    $js = \"\n        function nuMsgAfterPasswordChange() {\n            if (\'$changePassword\' == \'1\') {\n                nuLogout();\n            }\n        }\n        nuMessage(nuTranslate(\'Information\'), nuTranslate(\'Your password has been successfully changed\'), 1500, nuMsgAfterPasswordChange);\n    \";\n\n\n} else {\n    $msg = nuTranslate(\'\"New Password\" must be the same as \"Confirm New Password\"\');\n    $js = \"nuMessage(nuTranslate(\'Error\'), \'$msg\');\";\n}\n\n\nnuJavaScriptCallback($js);', 'hide', '', '1', '1', '0', '', NULL),
