('nuform_BS', 'nuform_BS', 'System PHP', 'nubuilder', '$o = \'#sfo_type#\';\n\nif (\'#sfo_type#\' == \'\') {\n    nuDisplayError(\'<b>Type</b> Cannot Be Blank..\', \'sfo_type\');\n}\nif (\'#sfo_description#\' == \'\') {\n    nuDisplayError(\'<b>Description</b> Cannot Be Blank..\', \'sfo_description\');\n}\n\n\n\n\nif ($o == \'browseedit\' || $o == \'subform\' || $o == \'browse\') {\n\n    if ($o != \'browse\') {\n        nuCheckTabs();\n    }\n\n    if ($o != \'subform\') {\n        nuCheckBrowse();\n    }\n\n    if (\'#sfo_browse_sql#\' == \'\') {\n        nuDisplayError(\'<b>Browse SQL</b> Cannot Be Blank..\', \'sfo_browse_sql\');\n    }\n}\n\nif ($o == \'edit\' or $o == \'launch\') {\n    nuCheckTabs();\n}\n\nif ($o != \'launch\') {\n    if (\'#sfo_table#\' == \'\') {\n        nuDisplayError(\'<b>Table Name</b> Cannot Be Blank..\', \'sfo_table\');\n    }\n    if (\'#sfo_primary_key#\' == \'\') {\n        nuDisplayError(\'<b>Primary Key</b> Cannot Be Blank..\', \'sfo_primary_key\');\n    }\n}\n\nfunction nuCheckBrowse() {\n\n    $r = 0;\n    $sf = nuSubformObject(\'zzzzsys_browse_sf\');\n\n    for ($i = 0; $i < count($sf->rows); $i++) {\n        if ($sf->deleted[$i] == 0) {\n            $r++;\n        }\n    }\n\n    if ($r == 0) {\n        nuDisplayError(\'<b>Must have at least 1</b> Browse Column Defined..\');\n    }\n\n}\n\nfunction nuCheckTabs() {\n\n    $r = 0;\n    $sf = nuSubformObject(\'zzzzsys_tab_sf\');\n\n    for ($i = 0; $i < count($sf->rows); $i++) {\n        if ($sf->deleted[$i] == 0) {\n            $r++;\n        }\n    }\n\n    if ($r == 0) {\n        nuDisplayError(\'<b>Must have at least 1</b> Tab Column Defined..\');\n    }\n\n}\n\n\nif (!empty($_POST[\'nuErrors\'])) {\n   nuSetValidationErrorTitle(\'Validation Error\');\n} ', '', '', '1', '0', '0', '', NULL),
