('nu5fdb1a7c5b3d3b2', 'nuBeforeSave', 'Before a record is saved, nuBuilder checks for the existance of a function called nuBeforeSave().\nIf this exists it will use its return value to decide whether to continue saving the record.', 'nubuilder', 'https://wiki.nubuilder.cloud/index.php?title=Javascript#nuBeforeSave', 'function nuBeforeSave() {\n\n    if (nuGetValue(\'cus_name\') !== \'something\') { /* condition(s) here */\n\n        nuMessage(\'Error message here.\');\n\n        // Cancel saving         \n        return false;\n\n    }\n\n    // Continue Saving\n    return true;\n\n}', 'JavaScript', '[\"0\"]', NULL, '2025-06-04 06:45:35', NULL),
('nu65f5feb50d4d041', 'nuDebug', NULL, 'nubuilder', NULL, 'nuDebug::setFlag(\'Test\')->log(\'Error: Failed to retrieve data..\');\n', 'PHP', '[\"3\"]', NULL, '2025-06-04 06:44:34', NULL),
('nu682683a594b7932', 'nuSelectBrowse', 'This is a built in function that runs when any record on a Browse Form is selected.', 'nubuilder', 'https://wiki.nubuilder.cloud/index.php?title=Javascript#nuSelectBrowse', 'function nuSelectBrowse(event, element) {\n\n    //  Example 1: Get info about the selected cell\n    const { pk, row, column, html, value, $cell } = nuBrowseRow(element);\n    console.log(pk, row, column, html, value, $cell);\n\n    // Example 2: Get the row number of the selected cell\n    const cell = nuBrowseRow(element);\n    console.log(cell.row);\n    \n    // Example 3: Get the value of column 2 in the same row\n    // 0 = 1st column, 1 = 2nd column or pass the column primary key\n    const otherCell = nuBrowseRow(element, 1); \n    console.log(otherCell.value);\n\n    // Open the current Form in a new browser tab\n    nuForm(nuFormId(), pk, \'\', \'\', \'2\');\n\n}', 'JavaScript', '[\"0\"]', NULL, '2025-06-04 06:44:40', NULL),
('nu6866e0177fc5399', 'nu_upload_file_uppy_code_template', NULL, 'nubuilder', NULL, '<div id=\"#uppy_div#\"></div>\n\n<script>\n\n$(document).ready(function () {\n    nuInitUppy();\n});\n\nfunction nuInitUppy() {\n\n    const $objId = $(\'#\' + \'#this_object_id#\');\n    const targetDivId = \'#uppy_div#\';\n    let uppyResponseMessage = \'\';\n\n    const uppy = nuUppyCreate();\n    uppy.use(Uppy.Dashboard, {\n        inline: true,\n        bundle: true,\n        height: $objId.nuCSSNumber(\'height\') || 400,\n        width: $objId.nuCSSNumber(\'width\') || \'100%\',\n        target: \'#\' + targetDivId,\n        showProgressDetails: true,\n        replaceTargetContent: true,\n        method: \'post\'\n    })\n    .use(Uppy.XHRUpload, {\n        endpoint: \'core/nuapi.php\',\n        shouldRetry: (xhr) => {\n            return false;\n        },\n        async onAfterResponse(xhr, response) {\n            try {\n                const jsonData = JSON.parse(xhr.responseText);\n                if (xhr.status === 401) {\n                    uppyResponseMessage = jsonData.message || \'Authentication failed.\';\n                    throw new Error(uppyResponseMessage);\n                }\n            } catch (e) {\n                uppyResponseMessage = \'Error processing server response.\';\n            }\n        }\n    });\n\n    uppy.on(\'upload\', (data) => {\n        uppy.setMeta({\n            procedure: \'nu_upload_file_template\',\n            session_id: window.nuSESSION,\n            record_id: nuRecordId()\n        });\n    });\n\n    uppy.on(\'complete\', (result) => {\n        uppyResponseMessage = \'\';\n\n        if (result.successful.length > 0) {\n            uppyResponseMessage = result.successful[0].response.body.message || \'File(s) uploaded successfully.\';\n        } else if (result.failed.length > 0) {\n            const firstFailed = result.failed[0];\n            uppyResponseMessage = firstFailed.response && firstFailed.response.body && firstFailed.response.body.message\n                ? firstFailed.response.body.message\n                : firstFailed.error.message || \'Error uploading file(s).\';\n        } else {\n            uppyResponseMessage = \'No files processed.\';\n        }\n\n        if (window.nuOnFileUploadComplete) {\n            nuOnFileUploadComplete(\'FS\', $objId.attr(\'id\'), result, uppyResponseMessage);\n        }\n    });\n\n    uppy.on(\'error\', (error) => {\n        console.error(\'Uppy error:\', error);\n    });\n}\n</script>\n', 'JavaScript', '[\"\"]', '1', '2025-07-04 03:32:59', NULL);
