('nuaddattribute', 'launch', 'nuaddattribute', 'Add attribute', 'nubuilder', '1', NULL, '', '', '', NULL, 0, 0, '0', NULL, 'SELECT * FROM Launch Form 0', '/*\n    0|autocomplete|\n    1|accesskey|\n    2|data-|\n    3|maxlength|\n    4|placeholder|\n    5|spellcheck|\n    6|title|\n    7|value|\n    8|nu-label-position\n    9|nu-disable-calendar\n    10|nu-tooltip\n    11|nu-tooltip-icon\n\n*/\n\nnuHide(\'plh_value_select\');\nnuHide(\'plh_attribute_name\');\nnuHideTabs(0);\nnuSelectRemoveEmpty(\'plh_attribute\', \'-1\');\nnuAddActionButton(\'nuAddAttribute\', \'Add\', \'addAttribute();\');\n\nfunction nuOnMobileViewComplete() {\n  const currentTop = $(\'#plh_value_text\').css(\'top\');\n  const currentTopLabel = $(\'#label_plh_value_text\').css(\'top\');\n  $(\'#plh_value_select\').css(\'top\', currentTop);\n  $(\'#label_plh_value_select\').css(\'top\',  currentTopLabel);\n}\n\nif (typeObj().val() !== \'input\') {\n    nuSelectRemoveOption(\'plh_attribute\', \'7\').val(\'\');\n}\n\nfunction onAttributeChanged(value) {\n\n    nuShow(\'plh_value_select\', value === \'0\' || value === \'5\' || value === \'8\');\n    nuShow(\'plh_value_text\', !(value === \'0\' || value === \'5\' || value === \'8\' || value === \'9\'));\n    nuShow(\'plh_attribute_name\', value === \'2\');\n    \n    nuSetValue(\'plh_value_text\',\'\');\n\n    if (value === \'0\') {\n        $(\"#plh_value_select\").empty();\n        addSelectOption(\'0\', \'on\');\n        addSelectOption(\'1\', \'off\');\n    } else if (value === \'5\') {\n        $(\"#plh_value_select\").empty();\n        addSelectOption(\'0\', \'true\');\n        addSelectOption(\'1\', \'false\');\n    } else if (value === \'4\') {\n        nuSetPlaceholder(\'plh_value_text\', \'Placeholder text\');\n    } else if (value === \'6\' || value === \'10\' || value === \'11\') {\n        nuSetPlaceholder(\'plh_value_text\', \'Tooltip text\');\n    } else if (value === \'2\') {\n        nuSetPlaceholder(\'plh_value_text\', \'String value\');\n        $(\'#plh_attribute_name\').trigger(\"focus\");\n    } else if (value === \'1\') {\n        nuSetPlaceholder(\'plh_value_text\', \'Single character\');\n    } else if (value === \'3\') {\n        nuSetPlaceholder(\'plh_value_text\', \'Number value\');\n    }  else if (value === \'7\') {\n        nuSetPlaceholder(\'plh_value_text\', \'String\');\n    }  else if (value === \'8\') {\n        $(\"#plh_value_select\").empty();\n        addSelectOption(\'top\', \'top\');\n        addSelectOption(\'custom\', \'custom\');\n    } \n\n}\n\nfunction addSelectOption(value, text) {\n    $(\'#plh_value_select\').append(\'<option value=\"\' + value + \'\">\' + text + \'</option>\');\n}\n\nfunction attributesObj() {\n    return $(\"#sob_input_attribute\", window.parent.document);\n}\n\nfunction typeObj() {\n    return $(\"#sob_all_type\", window.parent.document);\n}\n\n\nfunction valueObjText() {\n    return $(\'#plh_value_text\').is(\":visible\") ? nuGetValue(\'plh_value_text\') : nuGetValue(\'plh_value_select\', \'text\');\n}\n\nfunction addAttribute() {\n    const attributeValue = valueObjText();\n    const attributeName = nuGetValue(\'plh_attribute_name\');\n    const attributeType = nuGetValue(\'plh_attribute\');\n    const attributeText = nuGetValue(\'plh_attribute\', \'text\');\n    \n    if (attributeValue === \'\' && attributeType != 9 && attributeType != 2) {\n        nuMessage(nuTranslate(\'Error\'), nuTranslate(\"Missing value\"));\n        return;\n    }\n    \n    if (attributeName === \'\') {\n        nuMessage(nuTranslate(\'Error\'), nuTranslate(\"Missing name\"));\n        return;\n    }\n    \n    const attributeObj = attributesObj();\n    const attributeKey = attributeText + attributeName;\n    const attributeKeyWithEquals = attributeKey + \'=\"\';\n    \n    if (attributeObj.val().includes(attributeKeyWithEquals)) {\n        nuMessage(nuTranslate(\'Error\'), nuTranslate(\"The attribute already exists\"));\n        return;\n    }\n    \n    let kv = \'\';\n    if (attributeKeyWithEquals.includes(\'nu-disable-calendar\')) {\n        kv = \'nu-disable-calendar\';\n    } else {\n        kv = attributeKeyWithEquals + attributeValue + \'\"\';\n    }\n    \n    const currentVal = attributeObj.val();\n    if (currentVal === \'\') {\n        attributeObj.val(kv).change();\n    } else {\n        attributeObj.val(currentVal + \',\' + kv).change();\n    }\n}\n', NULL, NULL, NULL, '1', NULL),
('nuadddbcolumn', 'launch', 'nuadddbcolumn', 'Add Database Column', 'nubuilder', '1', NULL, '', '', '', NULL, 0, 0, '0', NULL, '', 'function nuAddDBColumnGetDataType(type, inputType, selectMultiple) {\n    \n    const BASE_TYPE_MAP = {\n        lookup: \'VARCHAR(25)\',\n        calc: \'DECIMAL(12,4)\',\n        textarea: \'TEXT\',\n    };\n\n    const SELECT_TYPE_MAP = {\n        single: \'VARCHAR(100)\',\n        multiple: \'VARCHAR(1000)\',\n    };\n\n    const INPUT_TYPE_MAP = {\n        date: \'DATE\',\n        nuDate: \'DATE\',\n        number: \'INT\',\n        nuAutoNumber: \'BIGINT UNSIGNED\',\n        nuNumber: \'DECIMAL(12,4)\',\n        file: \'LONGTEXT\',\n    };\n\n    if (type === \'select\') {\n        return SELECT_TYPE_MAP[selectMultiple ? \'multiple\' : \'single\'];\n    }\n\n    if (type === \'input\') {\n        return INPUT_TYPE_MAP[inputType] || \'VARCHAR(100)\';\n    }\n\n    return BASE_TYPE_MAP[type] || \'VARCHAR(100)\';\n    \n}\n\n\nfunction nuAddDBColumnSetDefault() {\n\n    const table = parent.$(\'#sob_all_table\').val();\n    const id = parent.$(\'#sob_all_id\').val();\n    const type = parent.$(\'#sob_all_type\').val();\n    const input = parent.$(\'#sob_input_type\').val();\n    /* const selectMultiple = parent.nuGetValue(\'sob_select_multiple\'); */ // Currently unused\n    const dataType = nuAddDBColumnGetDataType(type, input);\n\n    const query = `\\`${id}\\` ${dataType} NULL DEFAULT NULL`;\n    const alterStart = `ALTER TABLE \\`${table}\\` ADD`;\n\n    $(\'#sql_query_word\').html(alterStart);\n    $(\'#sql_query\').val(query);\n\n    nuSetProperty(\'sob_all_table\', table);\n    nuRefreshSelectObject(\'sql_after_column\');\n\n    return `${alterStart} ${query};`;\n\n}\n\nnuAddActionButton(\'Run\', \'Run\', \'nuHasNotBeenEdited(); nuRunPHPHidden(\"nu_run_add_db_column\")\');\nnuAddActionButton(\'Preview\', \'Preview\', \'nuAddDBColumnPreview()\');\nnuAddDBColumnSetDefault();\n\n\nfunction nuAddDBColumnPreview() {\n\n    const table = parent.$(\'#sob_all_table\').val();\n    const query = nuGetValue(\'sql_query\');\n    const after = nuGetValue(\'sql_after_column\');\n    let sql = \'ALTER TABLE `\' + table + \'`  ADD \' + query;\n\n    if (after != \'\') {\n        sql = sql + \" AFTER `\" + after + \'`\';\n    }\n\n    const escapeForHTMLAttribute = str => str.replace(/\\n/g, \'\\\\n\').replace(/\'/g, \"\\\\\'\");\n    const copyButtonHTML = (value, label) => `<button type=\"button\" class=\"nuActionButton nuAdminButton\" onclick=\"navigator.clipboard.writeText(nuTranslate(\'${escapeForHTMLAttribute(value)}\'))\">${nuTranslate(label)}</button>`;\n    const formIdCopyButton = copyButtonHTML(sql, \'Copy\');\n\n    nuMessage(\'Preview SQL\', sql + \';\' + \'<br></br>\' + formIdCopyButton);\n\n}\n\n$(\'#sql_query\').addClass(\'sql\');\n$(\'.sql\').on(\'dblclick\', function() {\n    nuOpenAce(\'SQL\', this.id);\n});', NULL, NULL, NULL, '1', NULL),
('nuauthentication', 'launch', 'nuauthentication', 'Authentication', 'nubuilder', '1', NULL, '', '', '', NULL, 0, 0, '0', NULL, '', 'function nuGet2FAProcedure() {\n    const d = nuDevMode();\n    const p = \'nu_authentication_2fa\';\n    return d ? p + \'_template\': p;\n}\n\nfunction nu2FAVerify() {\n    nuSetProperty(\'auth_code_verify\', $(\'#auth_code\').val());\n    nuSetProperty(\"nuauthcommand\", \"verify\");\n    const p = nuGet2FAProcedure();\n    nuRunPHPHidden(p);\n}\n\nfunction nu2FASendToken() {\n    nuSetProperty(\"nuauthcommand\", \"send\");\n    const p = nuGet2FAProcedure();\n    nuRunPHPHidden(p);\n}\n\nfunction handleEnterKey() {\n\n    $(\'#auth_code\').on(\'keydown\', function(evt) {\n        if (evt.key === \'Enter\') {\n            evt.preventDefault();\n            nu2FAVerify();\n        }\n    });\n\n}\n\nhandleEnterKey();\nnuHideHolders(0, 2);\n\n\n\n// Prevent [DOM] Password field is not contained in a form:\n$(\"#auth_code_verify\").wrap(\"<form id=\'nuFromVerif\' action=\'#\' method=\'post\' onsubmit=\'return false\'>\");', NULL, NULL, NULL, '1', NULL),
