('nubuildreport', 'browseedit', 'nubuildreport', 'Build Report', 'nubuilder', '1', '#sre_code#|New', 'zzzzsys_report', 'zzzzsys_report_id', '', NULL, 0, 0, '0', NULL, 'SELECT * FROM zzzzsys_report\nLEFT JOIN zzzzsys_form ON zzzzsys_form_id = sre_zzzzsys_form_id', 'if (nuFormType() == \'edit\') {\n\n    $(\'#sre_layout\').addClass(\'nuEdited\'); \n      nuAttachButtonImage(\'open_builder\',\'RD\');\n    \n    if (! nuIsNewRecord()) {\n  //    nuAddActionButton(\'Run\', \'Run\', \'nuRunReport(\"\'+ $(\'#sre_code\').val() +\'\")\');       \n    }\n    \n  }\n  \n  function nuBuildReportPickTableType(){\n      \n      var i   = $(\'#sre_zzzzsys_php_id\').val();\n      \n      var f   = \'\';\n      var r   = \'\';\n      \n      if(i.substr(0,10) == \'PROCEDURE:\'){\n          \n          f   = \'nuphp\';\n          r = i.substr(10);\n          \n      }\n      \n      if(i === \'\'){\n          \n          nuMessage([nuTranslate(\'No table selected\')]);\n          return;\n  \n      }\n      \n      if(i.substr(0,6) == \'TABLE:\'){\n          \n          nuVendorLogin(\'PMA\', sre_zzzzsys_php_iddescription.value)\n          return;\n  \n      }\n      \n      if(i.substr(0,4) == \'SQL:\'){\n          \n          f   = \'nuselect\';\n          r = i.substr(4);\n  \n      }\n      \n      nuPopup(f,r);\n      \n  }\n  \n  function nuBuildReportUpdateAclCount() {\n      var l = $(\"[data-nu-field=\'sre_zzzzsys_access_id\']\").length -2;\n      var t = l <= 0 ? \'\' : \' (\' + l + \')\';\n      $(\'#nuTab1\').html(nuTranslate(\'Access Level\') + t);\n  }', 'if (parent.nuCurrentProperties().form_id == \'nuaccess\') {\n    $(\'#nuBreadcrumb0\').html(nuTranslate(\'Reports\'));\n}\n\nnuBrowseStyleBadge(2); // Group', NULL, NULL, '1', NULL),
