('nucodesnippet', 'browseedit', 'nucodesnippet', 'Code Snippets', 'nubuilder', '1', '#cot_code#|New', 'zzzzsys_code_snippet', 'zzzzsys_code_snippet_id', '', NULL, 35, 10, '0', NULL, 'SELECT * FROM zzzzsys_code_snippet WHERE (\n\n		(IFNULL(cot_scope,\'\') LIKE \'%0%\' AND \'#IS_CUSTOM_CODE#\' = \'1\') OR\n		(IFNULL(cot_scope,\'\') LIKE \'%1%\' AND \'#IS_SETUP_HEADER#\' = \'1\') OR\n		(IFNULL(cot_scope,\'\') LIKE \'%2%\' AND \'#IS_SQL#\' = \'1\') OR\n		(IFNULL(cot_scope,\'\') LIKE \'%3%\' AND \'#IS_PHP#\' = \'1\') OR\n\n	(\n\n		LOCATE(\'#\', \'#IS_SETUP_HEADER#\') = 1 AND \n		LOCATE(\'#\', \'#IS_CUSTOM_CODE#\') = 1 AND\n		LOCATE(\'#\', \'#IS_PHP#\') = 1 AND \n		LOCATE(\'#\', \'#IS_SQL#\') = 1 \n\n	) \n)\n\nORDER BY cot_code ', NULL, 'nuBrowseStyleBadge(2); // Group', 'var sc = $(\'#cot_source_code\');\nsc.addClass(\'js\');\n\nsc.css(\'padding\', \'3px 3px 3px 3px\')\n\n// Add ACE event handlers\nsc.on(\'dblclick\', function() {\n    nuOpenAce(nuCodeSnippetGetSourceLangage(), this.id);\n});\n\n// Code Snippets form\nnuSetProperty(\'IS_SETUP_HEADER\', 0);\nnuSetProperty(\'IS_CUSTOM_CODE\', 1);\n\n// Disable nu-records\nif (String(nuRecordId()).startsWith(\'nu\') && !nuDevMode()) {\n    nuDisableAllObjects();\n    $(\'#nuSaveButton\').hide();\n}\n\nfunction nuCodeSnippetGetSourceLangage() {\n\n    let l = $(\'#cot_language\').val();\n    if (l === \'JavaScript\') l = \'JS\';\n    return l === \'\' ? \'JS\' : l;\n\n}\n\nif (nuCodeIsTemplate() && !nuDevMode()) {\n    nuDisableAllObjects();\n    nuMessage(\"Unable to save templates. Please clone them to create a new copy and save the cloned version instead.\", 2500);\n}\n\nfunction nuCodeIsTemplate() {\n    return nuGetValue(\'cot_template\');\n}\n\nfunction nuOnClone() {\n\n    let code = cot_code.value;\n\n    if (nuCodeIsTemplate()) {\n        code = code.substring(0, code.length - 9);\n        nuSetValue(\'cot_code\', code);\n        nuSetValue(\'cot_group\', \'\');\n        nuSetValue(\'cot_template\', false);\n       