('nuuser', 'browseedit', 'nuuser', 'Users', 'nubuilder', '1', '#sus_name#|New', 'zzzzsys_user', 'zzzzsys_user_id', '', NULL, 0, 0, '1', NULL, 'SELECT *\nFROM zzzzsys_user\nINNER JOIN zzzzsys_access ON zzzzsys_access_id = sus_zzzzsys_access_id\nWHERE\n(\n    (\n        ((\'#nu5fe0352637b2e4f_filter#\' LIKE \'#%\' OR (\'#nu5fe0352637b2e4f_filter#\' = \'\')) \n         AND (sus_expires_on > NOW() OR sus_expires_on IS NULL)) \n        OR (\'#nu5fe0352637b2e4f_filter#\' = \'Expired\' AND sus_expires_on < NOW())\n    )\n\n    AND nu_filter_match(\'sus_team\',\'nu5fd5b7fc8fad224\')\n    AND nu_filter_match(\'sus_position\',\'nu5fd5b7fc8de0c57\')\n    AND nu_filter_match(\'sus_department\',\'nu5fe035b0d058339\')\n    AND nu_filter_match(\'sus_language\',\'nu5bad6cb3746cc1b\')\n\n    AND\n    (CONCAT(sal_code,\' : \',sal_description) = \'#nu5bad6cb376141d0_filter#\' \n     OR LEFT(\'#nu5bad6cb376141d0_filter#\',1) IN (\'#\',\'\'))\n)\nORDER BY sus_name;\n', NULL, 'nuAddBrowseFilter(\'nu5fe0352637b2e4f\').nuSearchablePopup({ items: [\"Expired\"] }); // Status\nnuAddBrowseFilter(\'nu5bad6cb376141d0\').nuSearchablePopup({ items: nuUserFilterGetAccessLevel() }); // Access Level\nnuAddBrowseFilter(\'nu5bad6cb3746cc1b\').nuSearchablePopup({ items: nuUserFilterGetLanguage() }); // Language\nnuAddBrowseFilter(\'nu5fd5b7fc8de0c57\').nuSearchablePopup({ items: nuUserFilterGetPosition() }); // Position\nnuAddBrowseFilter(\'nu5fe035b0d058339\').nuSearchablePopup({ items: nuUserFilterGetDepartment() }); // Department\nnuAddBrowseFilter(\'nu5fd5b7fc8fad224\').nuSearchablePopup({ items: nuUserFilterGetTeam() }); // Team', 'jQuery.fn.extend({\n    nuWrapInForm: function(options = {}) {\n        const settings = Object.assign({\n            id: \'\',\n            action: \'\',\n            method: \'post\',\n            classes: \'\'\n        }, options);\n\n        const formAttrs = [\n            \'<form\',\n            settings.id ? ` id=\"${settings.id}\"` : \'\',\n            settings.action ? ` action=\"${settings.action}\"` : \'\',\n            settings.method ? ` method=\"${settings.method}\"` : \'\',\n            settings.classes ? ` class=\"${settings.classes}\"` : \'\',\n            \'></form>\'\n        ].join(\'\');\n\n        return this.wrapAll(formAttrs); // only called once for the full set\n    }\n});\n\n/*\n$(\'#new_password, #check_password\').nuWrapInForm({\n    id: \'passwordForm\',\n    action: \'\',\n    method: \'post\',\n    classes: \'my-form-class\'\n});\n*/\n\nnuSetPlaceholder(\'sus_code\', nuTranslate(\'(Optional)\'));\nnuSetToolTip(\'sus_code\', nuTranslate(\'E.g. Employee Id, Foreign Id etc. Leaving blank will set the \"Login Name\"\'));\nnuSetToolTip(\'sus_language\', nuTranslate(\'Leaving blank will use English\'));\n\nif (nuIsNewRecord()) {\n    nuHide(\'sus_zzzzsys_access_id_open_button\');\n    var password = nuRandomPassword(nuCharacterArray(false), 12);\n    nuSetValue(\'new_password\', password);\n    nuSetValue(\'check_password\', password);\n    nuHasNotBeenEdited();\n}\n\n$(\'#sus_zzzzsys_access_id_open_button\').toggleClass(\'input_button nuButton nuLookupButton\')\nnuSelectAddEnglishOption(\'sus_language\');\n\nnuHasNotBeenEdited();\n\nfunction nuBeforeSave() {\n\n    let code = $(\'#sus_code\');\n    if (code.val() === \'\') {\n        code.nuSetValue(nuGetValue(\'sus_login_name\'));\n    }\n\n    return true;\n\n}\n\nfunction nuUserSplitFullName(fullName) {\n\n    const name = fullName.split(\' \');\n    const person = {};\n\n    if (name.length > 1) {\n        person.lastName = name.pop();\n        person.firstName = name.join(\' \');\n    } else {\n        person.lastName = \"\";\n        person.firstName = fullName;\n    }\n\n    return person;\n\n}\n\nfunction nuUserSetNameParts(element) {\n\n    if (nuGetValue(\'sus_name\') === \'\') {\n        const firstName = nuGetValue(\'sus_first_name\');\n        const lastName = nuGetValue(\'sus_last_name\');\n        if (firstName !== \'\' && lastName !== \'\') {\n            nuSetValue(\'sus_name\', nuGetValue(\'sus_first_name\') + \' \' + nuGetValue(\'sus_last_name\'));\n        }\n        return;\n    }\n\n    const hasStar = element.value === \'*\';\n\n    if (element.id === \'sus_first_name\' && !hasStar) return;\n    if (element.id === \'sus_last_name\' && !hasStar) return;\n\n    if (!nuIsNewRecord() && !hasStar) return;\n\n    const nameParts = nuUserSplitFullName(nuGetValue(\'sus_name\'));\n    nuSetValue(\'sus_first_name\', nameParts.firstName, \'value\', false);\n    $(\'#sus_first_name\').addClass(\'nuEdited\');\n    nuSetValue(\'sus_last_name\', nameParts.lastName, \'value\', false);\n    $(\'#sus_last_name\').addClass(\'nuEdited\');\n\n}\n\nfunction nuTabPermissionsSetMarker() {\n    nuTabSetMarker(\'nu65d7dbb966b5d37\', \'sf_permission000upe_identcode\');\n}\n\nnuTabPermissionsSetMarker();', '[data-lastpass-icon-root], [data-lastpass-root] {\n    display: none !important;\n}', '1', NULL),
('nuappcloner', 'browseedit', 'nuappcloner', 'Application Cloner', 'nubuilder', '1', '#cla_code#|New', 'zzzzsys_app_cloner', 'zzzzsys_app_cloner_id', '', NULL, 0, 0, '0', NULL, 'SELECT * FROM zzzzsys_app_cloner', NULL, NULL, 'var pwOrgValue = nuGetValue(\'cla_target_password\');\n\nfunction nuAppClonerNoAutoFill(targetInputId) {\n\n    let tries = 0;\n    const maxTries = 40;\n\n    const clearAutofill = setInterval(() => {\n        document.querySelectorAll(\'input\').forEach(input => {\n            if (input.matches(\':-webkit-autofill\')) {\n                if (nuIsNewRecord()) {\n                    input.value = \'\';\n                } else {\n                    if (pwOrgValue !== input.value) {\n                        input.value = pwOrgValue;\n                    }\n                }\n                nuHasNotBeenEdited();\n            }\n        });\n\n        const target = document.getElementById(targetInputId);\n        if (target) target.focus();\n\n        if (++tries >= maxTries) {\n            clearInterval(clearAutofill);\n        }\n    }, 10);\n\n}\n\nfunction nuLoadCloneForm() {\n    // Get current database configuration\n    nuSetProperty(\'clone_source_db\', nuSERVERRESPONSE.database);\n\n    //    nuAppClonerNoAutoFill(\'cla_code\');\n\n    if (nuIsNewRecord()) {\n\n        // Set default values\n        nuSetValue(\'cla_target_host\', nuSERVERRESPONSE.host);\n        nuSetValue(\'cla_target_charset\', \'utf8mb4\');\n        nuSetValue(\'cla_target_port\', \'3306\');\n        nuSetValue(\'cla_insert_type\', \'INSERT\');\n        nuSetValue(\'cla_database_collation\', \'utf8mb4_unicode_ci\');\n        //   nuSetValue(\'cla_excluded_files\', \'nuconfig.php\');\n        // nuSelectSelectAll(\'cla_opt_2_include\'); // include all user tables/views\n\n\n        // Check all clone options by default\n        for (let i = 1; i <= 9; i++) {\n            nuSetValue(\'cla_opt_\' + i, \'checked\', true);\n        }\n\n        // Check default config options\n        nuSetValue(\'cla_show_progress\', \'checked\', true);\n        nuSetValue(\'cla_target_db_mode\', \'fail\', true);\n        nuSetValue(\'cla_copy_files_mode\', \'fail\', true);\n        nuSetValue(\'cla_copy_files\', \'checked\', true);\n\n        // Set source and target paths\n        nuSetValue(\'cla_source_path\', \'..\');\n\n        const db = nuSERVERRESPONSE.database;\n        nuSetValue(\'cla_target_path\', \'../../\' + nuCurrentDate() + \'_clone_\' + db);\n        nuSetValue(\'cla_target_db\', nuCurrentDate() + \'_clone_\' + db);\n\n\n        nuHasBeenEdited();\n    } else {\n        nuAppClonerHostChanged();\n    }\n\n\n    // Helper tooltips\n    nuSetToolTip(\'cla_dry_run_label\', \'Test the clone without making any changes\', true);\n    //   nuSetToolTip(\'cla_create_database_label\', \'Create target database if it doesn\\\'t exist (requires CREATE privilege)\', true);\n    nuSetToolTip(\'cla_transaction_mode_label\', \'Use transactions (may not work with DDL statements)\', true);\n    nuSetToolTip(\'cla_show_progress_label\', \'Show progress messages during cloning\', true);\n    nuSetToolTip(\'cla_opt_1\', \'CREATE statements for all nuBuilder system tables. E.g. zzzzsys_user, zzzzsys_access,..\', true);\n    nuSetToolTip(\'cla_opt_2\', \'CREATE statements for all non-nuBuilder tables and views. Does not start with prefix zzzzsys_*\', true);\n    nuSetToolTip(\'cla_opt_3\', \'INSERT statements for all nuBuilder system records (nu*). Primary key starts with nu*.\', true);\n    nuSetToolTip(\'cla_opt_4\', \'INSERT statements for all records in the zzzzsys tables which define the user\\\'s own application. Primary key does not start with nu*.\', true);\n    nuSetToolTip(\'cla_opt_5\', \'INSERT statements for all non-nuBuilder tables\', true);\n\n}\n\nnuLoadCloneForm();\n\n// Add action button to run the clone\nnuAddActionButton(\'RunHidden\', \'Run\', \'nuRunCloneApplication()\');\nnuAddActionButton(\'RunHiddenDry\', \'Dry Run\', \'nuRunCloneApplication(true)\');\nnuAttachFontAwesome(\'nuRunHiddenDryButton\', \'fa-width-auto fa-regular fa-lg fa-circle-play\')\n\n\n\n\n// Function to run the clone application\nfunction nuRunCloneApplication(dryRun = false) {\n\n    if (!nuIsSaved()) {\n        alert(nuTranslate(\'Form must be saved first..\'));\n        return;\n    }\n\n\n    // Validate required fields\n    const required = [\'clone_target_host\', \'clone_target_db\', \'clone_target_username\', \'clone_target_charset\'];\n    for (let field of required) {\n        if ($(\'#\' + field).val() === \'\' || $(\'#\' + field).val() === \'enter_your_value\') {\n            nuMessage([\'Please fill in all required target database fields\']);\n            $(\'#\' + field).focus();\n            return;\n        }\n    }\n\n    const progressId = nuID();\n\n    // Collect all parameters\n    const params = {\n        targetHost: nuGetValue(\'cla_target_host\'),\n        targetDB: nuGetValue(\'cla_target_db\'),\n        targetUsername: nuGetValue(\'cla_target_username\'),\n        targetPassword: nuGetValue(\'cla_target_password\'),\n        targetCharset: nuGetValue(\'cla_target_charset\'),\n        targetPort: nuGetValue(\'cla_target_port\'),\n        sourcePath: nuGetValue(\'cla_source_path\'),\n        targetPath: nuGetValue(\'cla_target_path\'),\n        insertType: nuGetValue(\'cla_insert_type\'),\n        // Clone options\n        opts: [],\n\n        // Configuration\n        config: {\n            progressId: progressId,\n            copyFiles: nuGetValue(\'cla_copy_files\'),\n            showProgress: nuGetValue(\'cla_show_progress\'),\n            dryRun: dryRun, // nuGetValue(\'cla_dry_run\') ,\n            databaseMode: nuGetValue(\'cla_target_db_mode\'),\n            fileMode: nuGetValue(\'cla_copy_files_mode\'),\n            transactionMode: nuGetValue(\'cla_transaction_mode\'),\n            logFile: nuGetValue(\'cla_log_file\') || null,\n            databaseCollation: nuGetValue(\'cla_database_collation\'),\n            excludedFiles: $(\'#cla_excluded_files\').val().split(\',\').map(f => f.trim()).filter(f => f),\n            excludedDirs: $(\'#cla_excluded_dirs\').val().split(\',\').map(d => d.trim()).filter(d => d),\n            includeTablesAndViews: nuSelectToArray(\'cla_opt_2_include\'),\n            excludeTablesAndViews: nuSelectToArray(\'cla_opt_2_exclude\')\n            //  excludedFiles: nuGetValue(\'cla_excluded_files\').split(\',\').map(f => f.trim()).filter(f => f),\n            //   excludedDirs: nuGetValue(\'cla_excluded_dirs\').split(\',\').map(d => d.trim()).filter(d => d)\n        }\n    };\n\n    // Collect checked options\n    for (let i = 1; i <= 9; i++) {\n        if ($(\'#cla_opt_\' + i).is(\':checked\')) {\n            params.opts.push(i);\n        }\n    }\n\n    if (params.opts.length === 0) {\n        nuMessage([\'Please select at least one clone option\']);\n        return;\n    }\n\n\n    if (!dryRun) {\n        // Confirm action\n        const message = params.config.dryRun ?\n            \'Run clone in DRY RUN mode? (No changes will be made)\' :\n            \'Clone the application to \' + params.targetDB + \'?\\n\\nThis will copy all selected data and files.\';\n\n        if (!confirm(message)) {\n            return;\n        }\n    }\n\n    nuRunPHPHiddenWithParams(\'nu_app_cloner\', \'nu_app_cloner_params\', params);\n\n    const msgBody = nuAppClonerCreateProgressMessage();\n    nuAppClonerGetProgress(msgBody, nuFormId(), progressId);\n\n}\n\nfunction nuAppClonerGetProgress1(msgBody, progressId) {\n\n    function fetchProgress() {\n        fetch(\'core/nuapi.php?progressId=\' + encodeURIComponent(progressId))\n            .then(r => r.text())\n            .then(text => {\n                nuAppClonerOutputProgress(msgBody, text);\n            });\n    }\n\n    // Start polling every 3 seconds\n    const intervalId = setInterval(fetchProgress, 3000);\n\n    // Stop polling after 30 seconds // change in LIVE!\n    setTimeout(() => {\n        clearInterval(intervalId);\n    }, 30000);\n\n}\n\nfunction nuAppClonerGetProgress(msgBody, formId, progressId) {\n\n    function fetchProgress() {\n        if (window.stopPolling === true || formId !== formId || nuFormType() !== \'edit\') {\n            clearInterval(intervalId);\n            return;\n        }\n\n        fetch(\'core/nuapi.php?progressId=\' + encodeURIComponent(progressId))\n            .then(r => r.text())\n            .then(text => {\n                nuAppClonerOutputProgress(msgBody, text);\n            });\n    }\n\n    const intervalId = setInterval(fetchProgress, 2000);\n\n    setTimeout(() => {\n        clearInterval(intervalId);\n    }, 1200000); // 20 minutes\n}\n\n\nfunction nuAppClonerCreateProgressMessage() {\n    const msgDiv = nuMessage(\'Application Cloner\', \'<b>0</b> Starting Cloning Process\');\n    const msgElement = msgDiv[0];\n\n    msgElement.style.maxHeight = \'600px\';\n    msgElement.style.width = \'400px\';\n    msgElement.style.overflow = \'hidden\';\n\n    const msgBody = msgElement.querySelector(\'.nuMessageBody\');\n\n    if (msgBody) {\n        Object.assign(msgBody.style, {\n            maxHeight: \'600px\',\n            overflowY: \'auto\',\n            display: \'block\',\n            textAlign: \'left\',\n            paddingLeft: \'30px\'\n        });\n    }\n\n\n    msgDiv.addClass(\'nuAppClonerProgress\');\n\n    return msgBody;\n}\n\nfunction nuAppClonerOutputReverseLines(input) {\n    return input\n        .split(\'\\n\') // Split by line breaks\n        .filter(line => line) // Remove empty lines (optional)\n        .reverse() // Reverse the array of lines\n        .join(\'\\n\'); // Join back with line breaks\n}\n\n\nfunction nuAppClonerOutputProgress(msgBody, text) {\n    const childDiv = msgBody.querySelector(\'div\');\n    if (childDiv) {\n        childDiv.innerHTML = nuAppClonerOutputReverseLines(text).replace(/\\n/g, \'<br>\');\n        childDiv.scrollTop = childDiv.scrollHeight; // Scroll to bottom\n    }\n}\n\n\nfunction nuAppClonerHostChanged() {\n    $(\'[data-nu-copy-files]\').nuEnable(nuSERVERRESPONSE.host === cla_target_host.value);\n}\n', '', '0', NULL),
('nuuserhome', 'launch', 'nuuserhome', 'User Home', 'nubuilder', '1', NULL, '', '', '', NULL, 0, 0, '0', NULL, '', 'nuUserHomeCheckNoObjectsOnForm();\n\nfunction openNuObject() {\n\n    $(\'#nuMessageDiv\').remove();\n    nuPopup(\'nuobject\', \'-1\', window.nuFORM.getCurrent().form_id);\n\n}\n\nfunction nuUserHomeCheckNoObjectsOnForm() {\n    if (nuSERVERRESPONSE.objects.length === 0 && window.global_access) {\n        const message = `${nuTranslate(\'There are currently no objects on this form.\')}&nbsp;&nbsp;\n            <a href=\"javascript:openNuObject();\">${nuTranslate(\'Start adding some\')}</a>.`;\n        \n        $.nuInfoBar.info(message, {autoClose : false, closeOnClick : true});\n    }\n}', NULL, NULL, NULL, NULL, NULL);
