('nuhome', 'launch', 'nuhome', 'Home', 'nubuilder', '1', NULL, '', '', '', NULL, 0, 0, '0', NULL, '', '//$(\'#user_home\').addClass(\'nuUserHomeButton\');\n$(\'.nuActionButton\').hide();\n\nnuGetStartingTab();\n\nfunction nuHomeOpenForm(formId, recordId, event) {\n    const target = event.ctrlKey ? \'2\' : \'0\';\n    nuForm(formId, recordId, \'\', \'\', target);\n}\n\n\nfunction nuHomeOpenCreateMenuConfig(menuType, event) {\n    const baseMenus = {\n        Procedure: [{\n                text: nuContextMenuItemText(\"Run\", \"fa-solid fa-play\"),\n                action: () => nuHomeOpenForm(\'nurunphp\', \'\', event)\n		},\n            {\n                text: nuContextMenuItemText(\"Add\", \"fa-solid fa-plus\"),\n                action: () => nuHomeOpenForm(\'nuprocedure\', \'-1\', event)\n		}\n		],\n        Notes: [{\n                text: nuContextMenuItemText(\"Add\", \"fa-solid fa-plus\"),\n                action: () => nuHomeOpenForm(\'nunotes\', \'-1\', event)\n		},\n            {\n                text: nuContextMenuItemText(\"Categories\", \"fa-solid fa-list\"),\n                action: () => nuHomeOpenForm(\'nunotescategroy\', \'\', event)\n		}\n		],\n        User: [{\n                text: nuContextMenuItemText(\"Add\", \"fa-solid fa-plus\"),\n                action: () => nuHomeOpenForm(\'nuuser\', \'-1\', event)\n		},\n            {\n                text: nuContextMenuItemText(\"Permission Items\", \"fa-solid fa-list\"),\n                action: () => nuHomeOpenForm(\'nupermissionitem\', \'\', event)\n		}\n		],\n        Email: [{\n                text: nuContextMenuItemText(\"Add Template\", \"fa-solid fa-plus\"),\n                action: () => nuHomeOpenForm(\'nuemailtemplate\', \'-1\', event)\n		},\n            {\n                text: nuContextMenuItemText(\"Email Templates\", \"fa-solid fa-list\"),\n                action: () => nuHomeOpenForm(\'nuemailtemplate\', \'\', event)\n		}\n		],\n        Report: [{\n                text: nuContextMenuItemText(\"Run\", \"fa-solid fa-play\"),\n                action: () => nuHomeOpenForm(\'nurunreport\', \'\', event)\n		},\n            {\n                text: nuContextMenuItemText(\"Add\", \"fa-solid fa-plus\"),\n                action: () => nuHomeOpenForm(\'nubuildreport\', \'-1\', event)\n		},\n            {\n                text: nuContextMenuItemText(\"Fast Report\", \"fa-solid fa-bolt\"),\n                action: () => nuHomeOpenForm(\'nufrlaunch\', \'\', event)\n		}\n		],\n        Form: [{\n                text: nuContextMenuItemText(\"Add\", \"fa-solid fa-plus\"),\n                action: () => nuHomeOpenForm(\'nuform\', \'-1\', event)\n		},\n            {\n                text: nuContextMenuItemText(\"Fast Form\", \"fa-solid fa-bolt\"),\n                action: () => nuHomeOpenForm(\'nufflaunch\', \'\', event)\n		}\n		],\n        Database: [{\n                text: nuContextMenuItemText(\"Sessions\", \"fa-solid fa-key\"),\n                action: () => nuHomeOpenForm(\'nusession\', \'\', event)\n		},\n            {\n                text: nuContextMenuItemText(\"Backup\", \"fa-regular fa-hard-drive\"),\n                action: () => nuRunBackup()\n		},\n            {\n                text: nuContextMenuItemText(nuTranslate(\"Cloner\") + \' (\' + nuTranslate(\"Forms\") + \')\', \"fa-regular fa-clone\"),\n                action: () => nuHomeOpenForm(\'nucloner\', \'\', event)\n		},\n            {\n                text: nuContextMenuItemText(nuTranslate(\"Cloner\") + \' (\' + nuTranslate(\"Application\") + \')\', \"fa-regular fa-clone\"),\n                action: () => nuHomeOpenForm(\'nuappcloner\', \'\', event)\n		},\n            {\n                text: nuContextMenuItemText(\"Update\", \"fa-solid fa-cloud-arrow-down\"),\n                action: () => nuHomeOpenForm(\'nuupdate\', \'\', event)\n		},\n            {\n                text: nuContextMenuItemText(\"CSV Transfer\", \"fa-solid fa-file-csv\"),\n                action: () => nuHomeOpenForm(\'nucsvtransf