('nuemailtemplate', 'browseedit', 'nuemailtemplate', 'Email Templates', 'nubuilder', '1', '#emt_code#|New', 'zzzzsys_email_template', 'zzzzsys_email_template_id', '', NULL, 0, 0, '0', NULL, 'SELECT\n  zzzzsys_email_template.*,\n  sfo_description\nFROM\n  zzzzsys_email_template\n  LEFT JOIN zzzzsys_form ON zzzzsys_form_id = emt_form_id\nWHERE\n  (\n    (\n      ifnull(emt_group, \'\') = \'#nu635294e972c3b80_filter#\'\n      OR left(\'#nu635294e972c3b80_filter#\', 1) IN (\'#\', \'\')\n    )\n    AND (\n      ifnull(emt_category, \'\') = \'#nu68784fb4ac37836_filter#\'\n      OR left(\'#nu68784fb4ac37836_filter#\', 1) IN (\'#\', \'\')\n    )\n  )', NULL, 'nuBrowseStyleBadge(1); // Group\n\nnuAddBrowseFilter(\'nu635294e972c3b80\').nuSearchablePopup({ items: nuEmailTemplateFilterGetGroup() });\n\nnuAddBrowseFilter(\'nu68784fb4ac37836\').nuSearchablePopup({ items: nuEmailTemplateFilterGetCategory() });\n', 'nuLabelOnTop([\'emt_form_id\']);\nactiveObj = $(\'#emt_body\');\n\n$(\'#emt_avail_fields\').nuLabelOnTop();\nnuSelectRemoveMultiple(\'emt_avail_fields\');\n\nif (nuEmailTemplateIsTemplate() && !nuDevMode()) {\n    nuDisableAllObjects();\n    nuMessage(`${nuTranslate(\'Error\')}`, `${nuTranslate(\"Unable to save templates. Please clone them to create a new copy and save the cloned version instead.\")}`, 3500);\n}\n\nnuEmailTemplateSetPreviewText();\n\nfunction nuEmailTemplateAddSelectedField() {\n    const selObjectId = nuGetValue(\'emt_avail_fields\', \'text\');\n    const selObjectLabel = nuGetValue(\'emt_avail_fields\');\n    if (selObjectId !== \'\') {\n        const activeObjectId = nuEmailTemplateGetActiveObjectId();\n        const isBody = activeObjectId == \'emt_body\';\n        const label = isBody ? \'<b>\' + selObjectLabel + \': </b>\' : \'\';\n        nuInsertAtCaret(activeObjectId, label + \"#\" + selObjectId + \"#\" + (isBody ? \'\\n\' : \'\'));\n        $(\'#\' + activeObjectId).trigger(\'change\');\n    }\n}\n\n\nfunction nuEmailTemplateWrapText(elementID