 });\n        });\n\n        // Function to programmatically select a navigation button\n        function nuSelectNavButton(targetId) {\n            const button = document.querySelector(`[data-target=\"${targetId}\"]`);\n            if (button) {\n                // Remove active class from all buttons\n                navButtons.forEach(btn => btn.classList.remove(\'active\'));\n                // Add active class to target button\n                button.classList.add(\'active\');\n                \n                // Hide all code areas\n                codeAreas.forEach(area => area.classList.remove(\'active\'));\n                \n                // Show target code area\n                const targetArea = document.getElementById(targetId + \'-area\');\n                if (targetArea) {\n                    targetArea.classList.add(\'active\');\n                }\n                \n                // Update header\n                const info = sectionInfo[targetId];\n                if (info) {\n                    currentDescription.textContent = info.description;\n                }\n                \n                return true; // Success\n            }\n            return false; // Button not found\n        }\n\n        // Function to get the currently active navigation button\n        function nuGetActiveNavButton() {\n            const activeButton = document.querySelector(\'.nu-nav-button.active\');\n            return activeButton ? activeButton.getAttribute(\'data-target\') : null;\n        }\n\n        // Handle textarea changes\n        document.querySelectorAll(\'.nu-code-textarea\').forEach(textarea => {\n            // Add nuEdited class on change\n            textarea.addEventListener(\'change\', function() {\n                this.classList.add(\'nuEdited\');\n                \n                // Map textarea ID to corresponding button data-target\n                const textareaToTarget = {\n                    \'nu-code-textarea-browse-edit\': \