('nu5fee06b1acbe4bb', 'nu_backup', 'Create a Database Backup using mysqldump', 'nubuilder', 'Backup', '1', '// Use the correct namespace\nuse Ifsnop\\Mysqldump\\Mysqldump;\n\n// Get the JSON settings passed from JS via the \'params\' property\n$params = nuGetProperty(\'NUBACKUP_params\');\n$settings = $params ? nuDecode($params)[\'settings\'] : [];\n\n// Call backup process using the decoded settings\nnuBackupRun($settings);\n\nfunction nuBackupRun($settings) {\n\n	nuBackupLoadMysqldumpLibrary(\'../third_party/mysqldump/Mysqldump.php\');\n\n	// Extract values with defaults using null coalescing operator\n	$directory = $settings[\'directory\'] ?? \'\';\n	$fileName = $settings[\'fileName\'] ?? \'\';\n	$includeTables = $settings[\'include-tables\'] ?? [];\n	$excludeTables = $settings[\'exclude-tables\'] ?? [];\n	$includeViews = $settings[\'include-views\'] ?? [];\n	$nuRecordsFilter = $settings[\'nuRecordsFilter\'] ?? \'\'; // nu, user, empty = all (default)\n	unset($settings[\'nuRecordsFilter\']);\n	\n	$nuTableFilter = $settings[\'nuTableFilter\'] ?? \'\'; // nu, user, empty = all (default)\n	unset($settings[\'nuTableFilter\']);\n\n	// Merge default dump settings with any settings passed from JS\n	$dumpSettings = nuBackupBuildDumpSettings($settings);\n\n	// Prepare the backup directory and filename\n	$backupDirectory = nuBackupDirectory($directory);\n	$backupFileName = nuBackupFileName($fileName, $dumpSettings);\n\n	// Set which tables to include/exclude\n	nuBackupSetTables($dumpSettings, $includeTables, $excludeTables, $includeViews, $nuTableFilter);\n\n	$dumper = nuBackupCreateDumper($dumpSettings);\n\n	$tableWheres = nuBackupBuildTableWheres($nuRecordsFilter);\n	$dumper->setTableWheres($tableWheres);\n\n    $backupDirectory = str_replace([\'/\', \'\\\\\'], DIRECTORY_SEPARATOR, $backupDirectory);\n	$dumpFile = $backupDirectory . nuSanitizeFilename($backupFileName);\n	nuBackupCreateDirectoryIfNotExists($backupDirectory);\n\n	nuBackupStartDump($dumper, $dumpFile);\n\n	nuBackupCompleteNotification($backupDirectory, $backupFileName);\n}\n\nfunction nuBackupFileExtension($dumpSettings) {\n	if (!array_key_exists(\'compress\', $dumpSettings) || $dumpSettings[\'compress\'] === \'None\' || $dumpSettings[\'compress\'] === \'\') {\n		return \'\';\n	}\n	return \'.\' . strtolower($dumpSettings[\'compress\']);\n}\n\nfunction nuBackupDirectory($directory) {\n    // Use provided directory or default\n    $backupDir = !empty($directory) ? $directory : \'../backup/\';\n\n    // Ensure the directory exists\n    if (!is_dir($backupDir)) {\n        mkdir($backupDir, 0755, true);\n    }\n\n    // Define the path to index.htm\n    $indexFile = rtrim($backupDir, \'/\\\\\') . \'/index.htm\';\n\n    // If index.htm doesn\'t exist, create it with a hidden comment\n    if (!file_exists($indexFile)) {\n        $comment = \"<!-- for security reasons, do not delete this file. -->\";\n        file_put_contents($indexFile, $comment);\n    }\n\n    return $backupDir;\n}\n\nfunction nuBackupFileName($fileName, $dumpSettings) {\n	// If fileName is provided, use it; otherwise generate a default filename\n	$extension = nuBackupFileExtension($dumpSettings);\n\n	return !empty($fileName)\n		? $fileName . $extension\n		: date(\'Y-m-d_H:i:s\') . \'_\' . uniqid() . \'_nuBuilder_backup.sql\' . $extension;\n\n}\n\nfunction nuBackupBuildDumpSettings($settings = []) {\n\n	$defaults = [\n		\'single-transaction\' => false,\n		\'no-create-info\' => false,\n		\'lock-tables\' => false,\n		\'add-locks\' => false,\n		\'extended-insert\' => false,\n		\'skip-definer\' => true,\n		\'routines\' => true,\n        \'events\' => true,\n		\'compress\' => $settings[\'compress\'] ?? \'None\' // Mysqldump::NONE\n	];\n\n	return array_merge($defaults, $settings);\n\n}\n\nfunction nuBackupLoadMysqldumpLibrary($path) {\n	try {\n		require_once($path);\n	} catch (Exception $e) {\n		nuDisplayError(\'require_once failed! Error: \' . $e->getMessage());\n	}\n}\n\nfunction nuBackupCreateDumper($dumpSettings) {\n	global $nuConfigDBHost,\n	$nuConfigDBName,\n	$nuConfigDBUser,\n	$nuConfigDBPassword,\n	$nuConfigDBPort;\n	$nuConfigDBPort = $nuConfigDBPort ?? \'3306\';\n\n	$dsn = \"mysql:host={$nuConfigDBHost};dbname={$nuConfigDBName};port={$nuConfigDBPort}\";\n	return new Ifsnop\\Mysqldump\\Mysqldump($dsn, $nuConfigDBUser, $nuConfigDBPassword, $dumpSettings);\n}\n\nfunction nuBackupBuildTableWheres($nuRecordsFilter = \'\') {\n	$tableWheres = [\n		\"zzzzsys_session\" => \"zzzzsys_session_id not like \'s%\'\"\n	];\n\n	if (in_array($nuRecordsFilter, [\'user\', \'nu\'])) {\n		$sysTables = nuGetSysTables();\n		$conditionFunction = ($nuRecordsFilter === \'user\') ? \'nuBackupTableIdNotLikeNu\' : \'nuBackupTableIdLikeNu\';\n		foreach ($sysTables as $tableName) {\n		    if ($tableName !== \'zzzzsys_session\') {\n			    $tableWheres[$tableName] = $conditionFunction($tableName);\n		    }\n		}\n	}\n\n	return $tableWheres;\n}\n\nfunction nuBackupTableIdNotLikeNu($tableName) {\n	return \"{$tableName}_id not like \'nu%\'\";\n}\n\nfunction nuBackupTableIdLikeNu($tableName) {\n	return \"{$tableName}_id like \'nu%\'\";\n}\n\nfunction nuBackupCreateDirectoryIfNotExists($directory) {\n	if (!is_dir($directory)) {\n		mkdir($directory, 0755, true);\n	}\n}\n\nfunction nuBackupStartDump($dumper, $dumpFile) {\n	try {\n		$dumper->start($dumpFile);\n	} catch (Exception $e) {\n		nuDisplayError(\'Export Error: \' . $e->getMessage());\n	}\n}\n\nfunction nuBackupCompleteNotification($backupDirectory, $backupFileName) {\n	$encodedPath = base64_encode($backupDirectory . $backupFileName);\n	$js = \"nuMessage([\'<h2>Export completed!</h2><br>SQL Dump saved in \' + atob(\'$encodedPath\')]);\";\n	nuJavaScriptCallback($js);\n}\n\nfunction nuBackupSetTables(&$dumpSettings, $includeTables, $excludeTables, $includeViews, $nuTableFilter) {\n\n	$dumpSettings[\"include-tables\"] = is_array($includeTables) ? $includeTables : [];\n	$dumpSettings[\"exclude-tables\"] = is_array($excludeTables) ? $excludeTables : [];\n	$dumpSettings[\"include-views\"] = is_array($includeViews) ? $includeViews : [];\n\n    if ($nuTableFilter === \'nu\') {\n        $dumpSettings[\"include-tables\"] = array_merge($dumpSettings[\"include-tables\"], nuGetSysTables());\n        $dumpSettings[\"exclude-tables\"] = array_merge($dumpSettings[\"exclude-tables\"], nuGetUserTables());\n    } else if ($nuTableFilter === \'user\') {\n        $dumpSettings[\"exclude-tables\"] = array_merge($dumpSettings[\"exclude-tables\"], nuGetSysTables());\n        $dumpSettings[\"include-tables\"] = array_merge($dumpSettings[\"exclude-tables\"], nuGetUserTables());\n    }\n\n}', NULL, NULL, 'hide', '', '1', '0', '0', '0', '', NULL),
('nubuildtable_BB', 'nubuildtable_BB', 'System PHP', 'nubuilder', NULL, '1', '$select = \"\n    SELECT\n     CONCAT(\n     \'PROCEDURE:\',\n     `zzzzsys_php`.`zzzzsys_php_id`\n     ) AS `zzzzsys_php_id`,\n     `zzzzsys_php`.`sph_code` ,\n     `zzzzsys_php`.`sph_description`\n    FROM\n     `zzzzsys_php`\n    WHERE\n     (\n     (`zzzzsys_php`.`sph_system` <> \'1\') \n     AND (LOCATE(\'{TABLE_ID}\', `zzzzsys_php`.`sph_php`) > \'0\') AND (`zzzzsys_php`.`sph_template` <> 1)\n     )\n     \n    UNION\n    \n    SELECT\n     CONCAT(\n     \'SQL:\',\n     `zzzzsys_select`.`zzzzsys_select_id`\n     ) AS `id`,\n     \'nuSQL\',\n     `zzzzsys_select`.`sse_description`\n    FROM\n     `zzzzsys_select`\n    WHERE\n     (\n     (\n     `zzzzsys_select`.`sse_system` IS NULL\n     ) OR(`zzzzsys_select`.`sse_system` = \'\')\n     )\n     \n    UNION\n    \n    SELECT\n     CONCAT(\n     \'TABLE:\',\n     `TABLE_NAME`\n     ) AS `id`,\n     \'nuTABLE\',\n     `TABLE_NAME` \n    FROM \n     `information_schema`.`tables` \n    WHERE \n     `TABLE_SCHEMA` = database()\n     AND `TABLE_NAME` NOT LIKE \'\\\\\\\\_%\' ESCAPE \'\\\\\\\\\'\n\";\n\n\n$select = str_replace(\"{TABLE_ID}\", \'#\'.\'TABLE_ID\'.\'#\', $select);\n\n$insert = \"\nINSERT INTO zzzzsys_report_data ( \n `zzzzsys_report_data_id`,\n `srd_code`,\n `srd_description`)\n\".$select;\n\n\nnuRunQuery(\'DELETE FROM `zzzzsys_report_data`\');\nnuRunQuery($insert);\n', NULL, NULL, NULL, NULL, '1', '0', '1', '0', NULL, NULL),
('nu5ff7efb1ed369a6', 'nu_refresh_select_object', 'Refresh a select object', 'nubuilder', 'Object', '1', 'function nuGetSelectValues($formId, $selectId) {\n\n    $sql = \"\n        SELECT\n            sob_select_sql\n        FROM\n            `zzzzsys_object`\n        WHERE\n            sob_all_zzzzsys_form_id = ? AND sob_all_id = ?\n    \";\n\n    $t = nuRunQuery($sql, [$formId, $selectId]);\n\n    $a = [];\n    if (db_num_rows($t) == 1) {\n\n        $r = db_fetch_row($t);\n        if ($r != false) {\n            \n            $disS = nuReplaceHashVariables($r[0]);\n\n            $t = nuRunQuery($disS);\n\n            while ($row = db_fetch_row($t)) {\n                $a[] = $row;\n            }\n\n            return json_encode($a);\n        }\n\n    }\n\n    return false;\n\n}\n\nfunction nuPopulateSelectObject($formId, $selectId, $removeBlank, $prefix) {\n\n    $j = nuGetSelectValues($formId, $selectId);\n\n    $selectId = $prefix.$selectId;\n\n    $cb = \"if (window.nuSelectObjectRefreshed) {\n    	    nuSelectObjectRefreshed(\'$formId\', \'$selectId\', nuSelectObjectRefreshedCount);\n         }\";\n\n    if ($j == false) {\n        \n        return \"var count = -1; \".$cb;\n        \n    } else {\n    	\n    return \"\n    	function nuPopulateSelectObject() {\n    	\n    		var p = $j;\n    \n    		$(\'#$selectId\').empty();\n    		\n    		if (\'$removeBlank\' == \'0\' ) {\n    		    $(\'#$selectId\').append(\'<option value=\\\"\\\"></option>\');\n    		}\n            \n            var count = 0;\n    \n    		if (p != \'\') {\n    		    var s = nuIsSaved();\n    			\n    			for (var i = 0; i < p.length; i++) {\n    				$(\'#$selectId\').append(\'<option value=\\\"\' + p[i][0] + \'\\\">\' + p[i][1] + \'</option>\');\n    				count ++;\n    			}\n    			\n    			if (s) { nuHasNotBeenEdited(); }\n    			\n    		}\n    		\n    		return count;\n    	}\n    	\n    	var nuSelectObjectRefreshedCount = nuPopulateSelectObject();\n\n    \".$cb;\n    }\n\n}\n\nfunction nu_refresh_select_object($selectId, $formIdHk, $removeBlank, $prefixHK) {\n\n    if (!nuHasProperty($formIdHk, $formId, false)) {\n        $formId = \'#form_id#\';\n    }\n\n    if (!nuHasProperty($prefixHK, $prefix, false)) {\n        $prefix = \'\';\n    }\n\n    $js = nuPopulateSelectObject($formId, $selectId, $removeBlank, $prefix);\n    nuJavaScriptCallback($js);\n\n}\n\nnu_refresh_select_object(\'#nu_refresh_select_object_selectid#\', \'nu_refresh_select_object_formid\', \'#nu_refresh_select_object_removeblank#\',\'nu_refresh_select_object_prefix\');', NULL, NULL, 'hide', '', '1', '1', '1', '0', '', NULL),
('nu5ff8249659aef00', 'nu_refresh_display_object', 'Refresh a display object', 'nubuilder', 'Object', '1', 'function nuGetDisplayValue($formId, $id) {\n\n    $sql = \"SELECT sob_display_sql, sob_display_procedure FROM `zzzzsys_object` WHERE sob_all_zzzzsys_form_id = ? AND sob_all_id = ?\";\n    $selectObject = nuRunQuery($sql, [$formId, $id]);\n\n    if (db_num_rows($selectObject) == 1) {\n        $obj = db_fetch_object($selectObject);\n\n        if ($obj != false) {\n\n            $displayProcedure = $obj->sob_display_procedure;\n\n            if (empty($displayProcedure)) {\n\n                $disS = nuReplaceHashVariables($obj->sob_display_sql);\n                $disT = nuRunQuery($disS);\n\n                if (db_num_rows($disT) >= 1) {\n                    $disR = db_fetch_row($disT);\n                    return $disR[0];\n                } else {\n                    return \"\";\n                }\n\n            } else {\n\n                $procedureCode = nuProcedure($displayProcedure);\n                if ($procedureCode !== \'\') {\n                    return nuEval($displayProcedure, $procedureCode);\n                } else {\n                    return false;\n                }\n            }\n        }\n    }\n\n    return false;\n}\n\n\nfunction nuRefreshDisplayObject() {\n\n    $HKPREFIX = \"nu_refresh_display_object_\";\n\n    if (!nuHasProperty(\"{$HKPREFIX}displayid\", $displayId, false)) {\n        return;\n    }\n\n    if (!nuHasProperty(\"{$HKPREFIX}formid\", $formId, false)) {\n        $formId = \'#form_id#\';\n    }\n\n    if (!nuHasProperty(\"{$HKPREFIX}prefix\", $prefix, false)) {\n        $prefix = \'\';\n    }\n\n\n    $value = nuGetDisplayValue($formId, $displayId);\n    $displayId = $prefix.$displayId;\n\n    if ($value === false && $value !== \'\') {\n        $js = \"nuMessage(nuTranslate(\'Error\'), nuTranslate(\'Failed to refresh the Display Object:\') + \' $displayId\'); \";\n    } else {\n\n        $value = base64_encode(json_encode($value));\n\n        $js = \"\n		    function nuRefreshDisplayObjectSetNewValue(displayId, value) {\n    			let obj = $(\'#\' + displayId);\n    			const format = obj.attr(\'data-nu-format\');\n    			const formattedValue = nuFORM.addFormatting(value, format);\n    			nuSetValue(displayId, formattedValue);\n		    }\n		    nuRefreshDisplayObjectSetNewValue(\'$displayId\', JSON.parse(atob(\'$value\')));\n			if (window.nuDisplayObjectRefreshed) {\n				nuDisplayObjectRefreshed(\'$displayId\', \'$formId\');\n			}\n		\";\n    }\n\n    nuJavaScriptCallback($js);\n\n}\n\nnuRefreshDisplayObject();', NULL, NULL, 'hide', '', '1', '1', '1', '0', '', NULL),
('nu6068bee1b6c63be', 'nu_run_add_db_column', 'Add a new database column', 'nubuilder', 'Database', '1', 'if (nuHash()[\'GLOBAL_ACCESS\'] == \'0\') return;\n\n$sql = \'ALTER TABLE `#sob_all_table#` ADD \' . \'#sql_query#\';\n$after = \'#sql_after_column#\';\nif ($after != \'\') {\n    $sql .= \" AFTER \" . $after;\n}\n\nif (preg_match(\'/DELETE |DROP |INSERT |;/\', strtoupper($sql))) {\n    $r = -2;\n} else {\n    $r = nuRunQuery($sql, [], true);\n}\n\nif ($r == 0) {\n    $js = \"$.nuInfoBar.success(nuTranslate(\'The column has been created successfully\')); \n           nuRefreshSelectObject(\'sql_after_column\'); \n           parent.nuUpdateMessage(\'refresh_required\');\";\n} else if ($r == -1) {\n    $js = \"$.nuInfoBar.error(nuTranslate(\'An error occurred while creating the column\') + \'. \' + nuTranslate(\'Check nuDebug Results for details\'));\";\n} else if ($r == -2) {\n    $js = \"$.nuInfoBar.error(nuTranslate(\'An error occurred while creating the column\') + \'. \' + nuTranslate(\'The query contains invalid keywords\'));\";\n}\n\nnuJavaScriptCallback($js);', NULL, NULL, 'hide', '', '1', '0', '0', '0', '', NULL),
('nu60f3f621d215470', 'nu_set_refresh_cache', 'Set marker to Refresh Form, Table schema', 'nubuilder', 'Session', '1', 'require(\'../nuconfig.php\');\n\nnuSetJSONDataAll(\'REFRESH_CACHE\',\'1\');\n\n$js = \"\n\nnuGetBreadcrumb();\n\nfunction nuMsgCacheRefreshed() {\n   nuMessage(nuTranslate(\'Information\'), nuTranslate(\'Cache Refreshed\'));\n}\nsetTimeout(nuMsgCacheRefreshed, 1000); \n\n\";\n\nnuJavaScriptCallback($js);\n\n', NULL, NULL, 'hide', '', '1', '0', '1', '0', '', NULL),
('nu60f8e2ea4d13aff', 'nu_import_users', 'Import users from a CSV file', 'nubuilder', 'User', '1', 'require_once(\'nusecurity.php\');\n\n$file = nuGetProperty(\'nu_import_users_file\');\n$delimiter = nuGetProperty(\'nu_import_users_delimiter\');\n\nnuImportUsersFromCSV(\"../temp/\".$file, $delimiter, \"\\n\");', NULL, NULL, 'window', NULL, '1', '0', '1', '0', '', NULL),
('nu610fd4ba4e73ede', 'nu_update_object', 'Update an Object\'s Properties', 'nubuilder', 'Object', '1', '$id = nuGetProperty(\'nu_update_object_id\');\n$value = nuGetProperty(\'nu_update_object_value\');\n$formId = nuGetProperty(\'nu_update_object_form_id\');\n$type = nuGetProperty(\'nu_update_object_type\');\n$column = nuGetProperty(\'nu_update_object_column\');\n$action = nuGetProperty(\'nu_update_object_action\');\n\nif ($action == \'move\') {\n\n        $query = \"\n            UPDATE\n                `zzzzsys_object`\n            SET\n                `sob_all_zzzzsys_tab_id` = ?\n            WHERE\n                `sob_all_zzzzsys_form_id` = ? AND `sob_all_id` = ?\n        \";\n\n        nuRunQuery($query, [$column, $formId, $id]);\n\n        $js = \"if (nuIsSaved()) {\n    	      nuGetBreadcrumb();\n    	   } else {\n    		  nuUpdateMessage(\'refresh_required\');\n    	}\";\n    	\n    	nuJavaScriptCallback($js);\n    	    \n        return;\n    \n}\n\nif ($action == \'delete\') {\n\n    $backupSQL = nuBuildInsertSQL(\n        \'zzzzsys_object\',\n        \'`sob_all_zzzzsys_form_id` = ? AND `sob_all_id` = ?\',\n        [$formId, $id]\n    );\n\n    nuDebug(\"Object $id Deleted in form $formId\", \"SQL to restore it:\", $backupSQL);\n\n    if ($type == \'tab\') {\n        // not in use yet\n\n        $query = \"\n            DELETE FROM `zzzzsys_tab`\n            WHERE `zzzzsys_tab_id` = ?\n        \";\n\n        nuRunQuery($query, [$id]);\n\n    } else if ($type == \'edit\') {\n\n        $query = \"\n            DELETE FROM `zzzzsys_object`\n            WHERE `sob_all_zzzzsys_form_id` = ? AND `sob_all_id` = ?\n        \";\n\n        nuRunQuery($query, [$formId, $id]);\n\n    } else {\n        // not in use yet\n\n        $query = \"\n            DELETE FROM `zzzzsys_browse`\n            WHERE `sbr_zzzzsys_form_id` = ? AND `sbr_order` = ?\n        \";\n\n        nuRunQuery($query, [$formId, $id]);\n\n    }\n\n\n    $js = \"nuHide(\'$id\')\";\n    nuJavaScriptCallback($js);\n\n    return;\n\n}\n\nif ($type == \'tab\') {\n\n    $query = \"\n            UPDATE `zzzzsys_tab`\n            SET $column = ?\n            WHERE `zzzzsys_tab_id` = ?\n    \";\n\n    nuRunQuery($query, [$value, $id]);\n\n} else if ($type == \'edit\') {\n\n    $query = \"\n        UPDATE `zzzzsys_object`\n        SET $column = ?\n        WHERE `sob_all_zzzzsys_form_id` = ? AND `sob_all_id` = ?\n    \";\n\n    nuRunQuery($query, [$value, $formId, $id]);\n\n} else {\n\n    $query = \"\n            UPDATE `zzzzsys_browse`\n            SET `$column` = ?\n            WHERE `sbr_zzzzsys_form_id` = ? AND `sbr_order` = ?\n    \";\n\n    nuRunQuery($query, [$value, $formId, $id]);\n\n}\n\nfunction nuBuildInsertSQL($table, $whereClause, $params) {\n    \n    $query = \"SELECT * FROM `$table` WHERE $whereClause\";\n    $result = nuRunQuery($query, $params);\n\n    if (db_num_rows($result) === 0) {\n        return null;\n    }\n\n    $row = db_fetch_array($result);\n\n    $columns = array_keys($row);\n\n    $values = array_map(function ($v) {\n        if (is_null($v)) {\n            return \'NULL\';\n        }\n        return \"\'\" . addslashes($v) . \"\'\";\n    },\n        array_values($row));\n\n    $colList = \'`\' . implode(\'`, `\',\n        $columns) . \'`\';\n    $valList = implode(\', \',\n        $values);\n\n    $insertSQL = \"INSERT INTO `$table` ($colList) VALUES ($valList);\";\n    return $insertSQL;\n    \n}', NULL, NULL, 'hide', NULL, '1', '0', '0', '0', '', NULL),
('nu61d8283e98e8b2e', 'nu_check_password_policy_template', 'Rules for enforcing Password policies', 'nubuilder', 'User', '1', 'function nuFATick($nok) {\n\n    $icon = $nok == false ? \"far fa-check-circle\" : \"far fa-times-circle\";\n    $color = $nok == false ? \"green\" : \"red\";\n    return \"&nbsp;&nbsp;<i class=\'$icon\' style=\'color:$color\'></i>&nbsp;&nbsp;\";\n\n}\n\nfunction nuCheckPasswordPolicy() {\n\n    $oldpw = \'#old_password#\';\n    $newpw = \'#new_password#\';\n\n    $atLeast = nuTranslate(\"Your New Password must contain at least\").\":\";\n    $msg = \'\';\n    $c = 0;\n\n    $check = strlen($newpw) < 8;\n    if ($check == true) $c++;\n    $msg .= nuFATick($check) . nuTranslate(\"8 Characters\") . \"<br>\";\n\n    $check = !preg_match(\"#[0-9]+#\", $newpw);\n    if ($check == true) $c++;\n    $msg .= nuFATick($check) . nuTranslate(\"1 Number\") . \"<br>\";\n\n    $check = !preg_match(\"#[A-Z]+#\", $newpw);\n    if ($check == true) $c++;\n    $msg .= nuFATick($check) . nuTranslate(\"1 Capital Letter\") . \"<br>\";\n\n    $check = !preg_match(\"#[a-z]+#\", $newpw);\n    if ($check == true) $c++;\n    $msg .= nuFATick($check) . nuTranslate(\"1 Lowercase Letter\") . \"<br>\";\n\n    $check = !preg_match(\'/[\\\'\\/~`\\!@#\\$%\\^&\\*\\(\\)_\\-\\+=\\{\\}\\[\\]\\|;:\"\\<\\>,\\.\\?\\\\\\]/\', $newpw);\n    if ($check == true) $c++;\n    $msg .= nuFATick($check) . nuTranslate(\"1 Special Character\") . \"<br>\";\n\n    if ($msg != $atLeast) $msg = $atLeast . \"<br><br>\" . $msg;\n\n    if ($newpw === $oldpw) {\n        $msg .= \"<br>\" . nuFATick(true) . nuTranslate(\"The provided New Password cannot be the same as the Current Password\") . \"<br>\";\n        $c++;\n    }\n\n    if ($c > 0) {\n        nuDisplayError($msg);\n        return false;\n    } else {\n        return true;\n    }\n\n}\n\n$check = nuCheckPasswordPolicy();', NULL, NULL, 'hide', '', '0', '1', '1', '1', '', NULL),
('nu61eceada0768c21', 'nu_change_password', 'Change user password', 'nubuilder', 'User', '1', '$oldPassword = \'#old_password#\';\n$newPasswordCheck = \'#new_password_check#\';\n$newPassword = \'#new_password#\';\n$session = \'#SESSION_ID#\';\n$userId = \"#USER_ID#\";\n\n\n\n//--\n\n$s = \"\n    SELECT sss_access\n    FROM zzzzsys_session\n    WHERE zzzzsys_session_id = ?\n\";\n\n$t = nuRunQuery($s, [$session]);\n$r = db_fetch_object($t);\n$j = json_decode($r->sss_access);\n\n//--\n\n$s = \"\n SELECT\n    sus_login_password,\n    sus_change_password\n FROM zzzzsys_user\n WHERE zzzzsys_user_id = ?\n\";\n\n$t = nuRunQuery($s, [$userId]);\n$r = db_fetch_object($t);\n$current = $r->sus_login_password;\n$changePassword = $r->sus_change_password;\n\n$useMd5 = $_SESSION[\'nubuilder_session_data\'][\'USE_MD5_PASSWORD_HASH\'] == true;\n\nif ($useMd5) {\n    $check = md5($oldPassword) == $current;\n} else {\n    $check = password_verify($oldPassword, $current);\n}\n\nif ($check == false) {\n    nuDisplayError(\'<h2>\' . nuTranslate(\'Error\') . \'</h2>\' . nuTranslate(\'Incorrect Password\'));\n    return;\n}\n\nif ($oldPassword == $newPassword) {\n\n    nuDisplayError(\'<h2>\' . nuTranslate(\'Error\') . \'</h2>\' . nuTranslate(\'Your new password cannot be the same as your old password\'));\n    return;\n}\n\nif ($oldPassword === \'\' || $newPassword === \'\') {\n    nuDisplayError(\'<h2>\' . nuTranslate(\'Error\') . \'</h2>\' . nuTranslate(\'The password cannot be left blank\'));\n    return;\n}\n\n\n//--\n\n$p = nuProcedure(\'nu_check_password_policy\');\n\nif ($p != \'\') {\n\n    eval($p);\n\n    if ($check == false) {\n        return;\n    }\n\n}\n\n//--\n\nif ($newPassword == $newPasswordCheck) {\n\n    $s = \"\n\n         UPDATE zzzzsys_user\n         SET\n            sus_login_password = ?,\n            sus_change_password = ?\n         WHERE zzzzsys_user_id = ?\n    \";\n\n    $pwHash = $useMd5 == true ? md5($newPassword) : nuPasswordHash($newPassword);\n\n    nuRunQuery($s, [$pwHash, \'0\', $userId]);\n\n    nuSetUserJSONData(\'PASSWORD_CHANGED_TIME\', time(), $userId);\n    nuSetUserJSONData(\'PASSWORD_CHANGED_SOURCE\', \'user\', $userId);\n\n\n\n    if (nuObjKey($_SESSION[\'nubuilder_session_data\'], \'SESSION_CHANGE_PW_STATUS\') == \'PENDING\') {\n        $_SESSION[\'nubuilder_session_data\'][\'SESSION_CHANGE_PW_STATUS\'] = \'CHANGED\';\n    }\n\n    $js = \"\n        function nuMsgAfterPasswordChange() {\n            if (\'$changePassword\' == \'1\') {\n                nuLogout();\n            }\n        }\n        nuMessage(nuTranslate(\'Information\'), nuTranslate(\'Your password has been successfully changed\'), 1500, nuMsgAfterPasswordChange);\n    \";\n\n\n} else {\n    $msg = nuTranslate(\'\"New Password\" must be the same as \"Confirm New Password\"\');\n    $js = \"nuMessage(nuTranslate(\'Error\'), \'$msg\');\";\n}\n\n\nnuJavaScriptCallback($js);', NULL, NULL, 'hide', '', '1', '1', '1', '0', '', NULL),
('nu62afe0e1933ee3b_BE', 'nu62afe0e1933ee3b_BE', 'System PHP', 'nubuilder', NULL, '1', 'require_once(\'nusetuplibs.php\');\n\n$config = nuConfigScript();\n$configCode = $config[\'code\'];\n$configJS = $config[\'js\'];\n\n$configJS = $configJS == null ? \"\" : $configJS. \" \" . \"nuHide(\'nuconfigsettingsfromdb_hint\'); \";\n\n\nif ($configCode != null) {\n    eval($config[\'code\']);\n}\n\nnuAddJavaScript($configJS);\n\n\nif (isset($_SESSION[\'nubuilder_session_data\'][\'USER_ADDITIONAL1_LABEL\']) && isset($nuConfigUserAdditional1Label)) {\n    $_SESSION[\'nubuilder_session_data\'][\'USER_ADDITIONAL1_LABEL\'] = $nuConfigUserAdditional1Label;\n}    \n\nif (isset($_SESSION[\'nubuilder_session_data\'][\'USER_ADDITIONAL2_LABEL\']) && isset($nuConfigUserAdditional2Label)) {\n    $_SESSION[\'nubuilder_session_data\'][\'USER_ADDITIONAL2_LABEL\'] = $nuConfigUserAdditional2Label;\n}\n\nif (isset($_SESSION[\'nubuilder_session_data\'][\'USER_CODE_LABEL\']) && isset($nuConfigUserCodeLabel)) {\n    $_SESSION[\'nubuilder_session_data\'][\'USER_CODE_LABEL\'] = $nuConfigUserCodeLabel;\n}\n\nnuAddJavaScript (\" \n   nuSERVERRESPONSE.buttons.Clone = \'0\';\n   nuSERVERRESPONSE.buttons.Delete = \'0\';\n\", true);', NULL, NULL, NULL, NULL, '1', '0', '1', '0', NULL, NULL),
('nuauthentication_BE', 'nuauthentication_BE', 'System PHP', 'nubuilder', NULL, '1', '$devMode = nuReplaceHashVariables(\"#DEV_MODE#\");\n\n$t = $devMode == \'1\' ? \'_template\' : \'\';\n\n$p  = nuProcedure(\'nu_authentication_2fa\'.$t);	\n\nif($p != \'\') { \n    eval($p); \n} else {\n    nuDisplayError(nuTranslate(\'The Procedure nu_authentication_2fa does not exist.\'));    \n}', NULL, NULL, NULL, NULL, '1', '0', '1', '0', NULL, NULL),
('nucalcobjects_BB', 'nucalcobjects_BB', 'System PHP', 'nubuilder', NULL, '1', '$select = \"\n\n    SELECT \n       sob_all_zzzzsys_form_id AS theform,\n       \'\' AS theparent,\n       sob_all_id AS thechild,\n       CONCAT(sob_all_id) as thevalue\n    FROM zzzzsys_object \n    WHERE sob_input_type = \'number\'\n    OR sob_all_type = \'calc\'\n    \n    UNION \n    \n    SELECT \n       su.sob_all_zzzzsys_form_id AS theform,\n       su.sob_all_id AS theparent,\n       inp.sob_all_id AS thechild,\n       CONCAT(su.sob_all_id, \'.\', inp.sob_all_id) as thevalue\n    FROM zzzzsys_object AS su\n    JOIN zzzzsys_object AS inp ON su.sob_subform_zzzzsys_form_id = inp.sob_all_zzzzsys_form_id\n    WHERE su.sob_all_type = \'subform\'\n    AND (\n            inp.sob_input_type = \'nuNumber\' OR \n            inp.sob_input_type = \'number\' OR \n            inp.sob_all_type = \'calc\'\n        )\n\n\";\n    \nnuCreateTableFromSelect(\'#TABLE_ID#\', $select);', NULL, NULL, '', '', '1', '0', '1', '0', '', NULL);
INSERT INTO `zzzzsys_php` (`zzzzsys_php_id`, `sph_code`, `sph_description`, `sph_group`, `sph_category`, `sph_status`, `sph_php`, `sph_javascript`, `sph_javascript_active`, `sph_run`, `sph_zzzzsys_form_id`, `sph_system`, `sph_global`, `sph_demo`, `sph_template`, `sph_hide`, `sph_json`) VALUES
('nuclonerphp', 'nu_cloner', 'Clone Forms, Objects, Events, PHP etc.', 'nubuilder', 'Cloner', '1', 'function hashCookieSet($h) {\n    return !(preg_match(\'/\\#(.*)\\#/\', $h) || trim($h) == \"\");\n}\n\nfunction pkWithoutEvent($pk) {\n    return substr($pk, 0, -3);\n}\n\nfunction eventFromPk($pk) {\n    return substr($pk, -3);\n}\n\nfunction lookupPk($arr, $key) {\n    \n    $a = array_column($arr, $key);\n    return isset($a[0]) ? $a [0] : \'\';\n\n}\n\nfunction addToArray(array &$arr, $key, $value) {\n    array_push($arr, [$key => $value]);\n}\n\nfunction getPk($pk) {\n    return \"#cloner_new_pks#\" != \'0\' ? nuID() : $pk;\n}\n\nfunction getTabList() {\n\n    $t = \"#cloner_tabs#\";\n    return !hashCookieSet($t) || strlen($t) < 3 ? \"\" : implode(\',\', json_decode($t));\n\n}\n\nfunction getSubformList() {\n\n    $t = \"#cloner_subforms#\";\n    return !hashCookieSet($t) || strlen($t) < 3 ? \"\" : \"\'\".implode(\"\',\'\", json_decode($t)).\"\'\";\n\n}\n\nfunction getIframeFormList() {\n\n    $t = \"#cloner_iframe_forms#\";\n    return !hashCookieSet($t) || strlen($t) < 3 ? \"\" : \"\'\".implode(\"\',\'\", json_decode($t)).\"\'\";\n\n}\n\nfunction getFormSource(&$f1) {\n\n    $f1 = \"#cloner_form_source#\";\n    if (!hashCookieSet($f1)) {\n        $f1 = \"#form_id#\";\n        return true;\n    }\n\n    return formExists($f1);\n\n}\n\nfunction getFormDestination(&$f2) {\n\n    $f2 = \"#cloner_form_dest#\";\n    if (!hashCookieSet($f2)) {\n        $f2 = \"\";\n        return true;\n    }\n\n    return formExists($f2);\n\n}\n\nfunction formSQL() {\n    return \"SELECT * FROM zzzzsys_form WHERE zzzzsys_form_id  = ? LIMIT 1\";\n}\n\nfunction formExists($f) {\n\n    $t = nuRunQuery(formSQL(), [$f]);\n    return db_num_rows($t) == 1;\n\n}\n\nfunction echoPlainText($val) {\n\n    echo \'<pre>\';\n    echo htmlspecialchars($val);\n    echo \'</pre>\';\n\n}\n\nfunction dumpFormInfo($f, $dump) {\n\n    if ($dump != \'1\') return;\n\n    $fi = getFormInfo($f);\n    echo \"<b>\";\n    echo \"-- nuBuilder cloner SQL Dump \" . \"<br>\";\n    echo \"-- Version 1.21 \" . \"<br>\";\n    echo \"-- Generation Time: \" . date(\"F d, Y h:i:s A\") . \"<br><br>\";\n    echo \"-- Form Description: \" . $fi[\"description\"] . \"<br>\";\n    echo \"-- Form Code: \" . $fi[\"code\"] . \"<br>\";\n    echo \"-- Form Table: \" . $fi[\"table\"] . \"<br>\";\n    echo \"-- Form Type: \" . $fi[\"type\"] . \"<br><br>\";\n\n    $notes = \"#cloner_notes#\";\n    echo hashCookieSet($notes) ? \"-- Notes: \" . $notes . \"<br>\" . \"<br>\" : \"\";\n    echo \"</b>\";\n\n}\n\nfunction createStatement($table, $columns, $row) {\n\n    $params = array_map(function ($val) {\n        return \"?\";\n    }\n    , $row);\n\n    return getStatement() . \"$table (\" . implode(\', \', $columns) . \") VALUES ( \" . implode(\" , \", $params) . \" ) \";\n\n}\n\nfunction echoHeader($header) {\n\n    echo \"<br>--<br>\";\n    echo \"-- <b>\" . $header . \"</b><br>\";\n    echo \"--<br><br>\";\n    \n}\n\nfunction getStatement() {\n    return \"#cloner_replace_into#\" == \'1\' ? \'REPLACE INTO \' : \'INSERT INTO \';\n}\n\nfunction dumpStatement($table, $row, &$first, $ident) {\n\n    $values = join(\', \', array_map(function ($value) {\n        return $value === null ? \'NULL\' : db_quote($value);\n    }\n    , $row));\n\n    if (!isset($first)) {\n        $ident = $ident == \'\' ? \'\' : \': \'. $ident;\n        echoHeader($table. $ident);\n        $first = false;\n    }\n\n    echoPlainText(getStatement() . \"$table (\" . implode(\', \', array_keys($row)) . \") \");\n    echoPlainText(\"VALUES ( \" . $values . \" ); \");\n\n}\n\n\nfunction writeRecord($table, $row, &$first, $ident) {\n\n    if (\"#cloner_dump#\" == \'1\') {\n         dumpStatement($table, $row, $first, $ident);\n    }\n    else {\n        $i = createStatement($table, array_keys($row) , $row);\n        nuRunQuery($i, array_values($row) , true);\n    }\n\n}\n\nfunction getFormType($f) {\n\n    $t = nuRunQuery(formSQL() , [$f]);\n    $row = db_fetch_array($t);\n\n    return $row[\'sfo_type\'];\n\n}\n\nfunction getFormInfo($f) {\n\n    $t = nuRunQuery(formSQL() , [$f]);\n    $row = db_fetch_object($t);\n\n    return [\n        \"code\" => $row->sfo_code,\n        \"description\" => $row->sfo_description,\n        \"type\" => $row->sfo_type,\n        \"table\" => $row->sfo_table\n    ];\n\n}\n\nfunction getNewFormCode($code) {\n\n    if (\"#cloner_new_pks#\" == \'0\') {\n        return $code;\n    } else {    \n        $s = \"SELECT COUNT(zzzzsys_form_id) + 1 FROM zzzzsys_form WHERE sfo_code LIKE ?\";\n        $t = nuRunQuery($s, [$code . \'_clone%\']);\n        $r = db_fetch_row($t);\n        return $code . \'_clone_\' . $r[0];\n    }\n\n}\n\nfunction cloneForm($f1) {\n\n    $t = nuRunQuery(formSQL(), [$f1]);\n    $row = db_fetch_array($t);\n\n    $newPk = getPk($row[\'zzzzsys_form_id\']);\n    $row[\'zzzzsys_form_id\'] = $newPk;\n    $row[\'sfo_code\'] = getNewFormCode($row[\'sfo_code\']);\n\n    writeRecord(\'zzzzsys_form\', $row, $first, $row[\'sfo_code\']);\n\n    return $newPk;\n\n}\n\nfunction cloneFormPHP($f1, $f2) {\n\n    $s = \"\n        SELECT\n            zzzzsys_php.*\n        FROM\n            zzzzsys_php\n        LEFT JOIN zzzzsys_form ON zzzzsys_form_id = LEFT(zzzzsys_php_id, LENGTH(zzzzsys_php_id) - 3)\n        WHERE\n            zzzzsys_form_id = ?\n	\";\n\n    $t = nuRunQuery($s, [$f1]);\n\n    while ($row = db_fetch_array($t)) {\n\n        $event = eventFromPk($row[\'zzzzsys_php_id\']);\n        $row[\'zzzzsys_php_id\'] = $f2 . $event;\n        $row[\'sph_code\'] = $f2 . $event;\n\n        writeRecord(\'zzzzsys_php\', $row, $first, \'\');\n\n    }\n\n}\n\nfunction cloneFormTabs($f1, $f2, $postifx = \'\') {\n\n    $tabPks = [];\n    \n    $s = \"SELECT * FROM zzzzsys_tab AS tab1 WHERE syt_zzzzsys_form_id  = ?\".whereTabs();\n    $t = nuRunQuery($s, [$f1]);\n\n    while ($row = db_fetch_array($t)) {\n\n        $newPk = getPk($row[\'zzzzsys_tab_id\']);\n        addToArray($tabPks, $row[\'zzzzsys_tab_id\'], $newPk);\n        $row[\'zzzzsys_tab_id\'] = $newPk;\n        $row[\'syt_title\'] = $row[\'syt_title\'].$postifx;\n        $row[\'syt_zzzzsys_form_id\'] = $f2;\n\n        writeRecord(\'zzzzsys_tab\', $row, $first, \'\');\n\n    }\n\n    return $tabPks;\n\n}\n\nfunction cloneFormBrowse($f1, $f2) {\n\n    $s = \"SELECT * FROM zzzzsys_browse WHERE sbr_zzzzsys_form_id  = ?\";\n    $t = nuRunQuery($s, [$f1]);\n\n    while ($row = db_fetch_array($t)) {\n\n        $newPk = getPk($row[\'zzzzsys_browse_id\']);\n        $row[\'zzzzsys_browse_id\'] = $newPk;\n        $row[\'sbr_zzzzsys_form_id\'] = $f2;\n\n        writeRecord(\'zzzzsys_browse\', $row, $first, \'\');\n\n    }\n\n}\n\nfunction whereTabs() {\n    \n    $tabs = getTabList();\n    \n    return $tabs != \'\' ? \" AND tab1.syt_order DIV 10 IN ($tabs) \" : \"\";\n    \n}\n\n\nfunction getTabIds($f1, $f2) {\n\n    $s = \"    \n        SELECT\n            tab1.zzzzsys_tab_id AS tab1,\n            tab2.zzzzsys_tab_id AS tab2\n        FROM\n            zzzzsys_tab AS tab1\n        LEFT JOIN zzzzsys_tab AS tab2\n        ON\n            tab1.syt_order = tab2.syt_order\n        WHERE\n            tab1.syt_zzzzsys_form_id = ? AND tab2.syt_zzzzsys_form_id = ? \n    \".whereTabs();\n\n    $t = nuRunQuery($s, [$f1, $f2]);\n\n    $tabPks = [];\n    while ($r = db_fetch_object($t)) {\n        addToArray($tabPks, $r->tab1, $r->tab2);\n    }\n\n    return $tabPks;\n\n}\n\nfunction cloneObjects($f1, $f2, array &$objectPks, $tabPks) {\n\n    $s = \"SELECT * FROM zzzzsys_object WHERE sob_all_zzzzsys_form_id = ?\";\n    $t = nuRunQuery($s, [$f1]);\n  \n    while ($row = db_fetch_array($t)) {\n\n        $row[\'sob_all_zzzzsys_form_id\'] = $f2;\n\n        $newPk = getPk($row[\'zzzzsys_object_id\']);\n        addToArray($objectPks, $row[\'zzzzsys_object_id\'], $newPk);\n\n        $row[\'zzzzsys_object_id\'] = $newPk;\n\n        $tabId = lookupPk($tabPks, $row[\'sob_all_zzzzsys_tab_id\']);\n\n        $row[\'sob_all_zzzzsys_tab_id\'] = $tabId;\n\n        if ($tabId != \"\") writeRecord(\'zzzzsys_object\', $row, $first,\'\');\n\n    }\n\n}\n\nfunction cloneObjectsPHP($f1, $objectPks) {\n\n    $s = \"\n        SELECT\n           zzzzsys_php.* \n        FROM\n           zzzzsys_php \n           LEFT JOIN\n              zzzzsys_object \n              ON zzzzsys_object_id = LEFT(zzzzsys_php_id, LENGTH(zzzzsys_php_id) - 3) \n        WHERE\n           sob_all_zzzzsys_form_id = ?\n	\";\n\n    $t = nuRunQuery($s, [$f1]);\n\n    while ($row = db_fetch_array($t)) {\n\n        $event = eventFromPk($row[\'zzzzsys_php_id\']);\n        $row[\'zzzzsys_php_id\'] = lookupPk($objectPks, pkWithoutEvent($row[\'zzzzsys_php_id\'])) . $event;\n        $code = lookupPk($objectPks, pkWithoutEvent($row[\'sph_code\']));\n        if ($code == \'\') { $row[\'sph_code\'] = $row[\'zzzzsys_php_id\']; }\n        $code .=  $event;\n\n        writeRecord(\'zzzzsys_php\', $row, $first, \'\');\n\n    }\n\n}\n\nfunction cloneFormSelect($f1, $f2, array &$formSelectPks) {\n\n    $s = \"\n        SELECT\n           zzzzsys_select.* \n        FROM\n           zzzzsys_select \n        WHERE LEFT(zzzzsys_select_id, LENGTH(zzzzsys_select_id) - 3)  = ?\n	\";\n\n    $t = nuRunQuery($s, [$f1]);\n\n    while ($row = db_fetch_array($t)) {\n\n        $event = eventFromPk($row[\'zzzzsys_select_id\']);\n        $newPk = $f2 . $event;\n        addToArray($formSelectPks, $row[\'zzzzsys_select_id\'], $newPk);\n        $row[\'zzzzsys_select_id\'] = $newPk;\n\n        writeRecord(\'zzzzsys_select\', $row, $first, \'\');\n\n    }\n\n}\n\nfunction cloneFormSelectClause($f1, $formSelectPks) {\n\n    $s = \"\n        SELECT\n           zzzzsys_select_clause.* \n        FROM\n           zzzzsys_select_clause \n           LEFT JOIN\n              zzzzsys_select \n              ON zzzzsys_select_id = ssc_zzzzsys_select_id \n           LEFT JOIN zzzzsys_form ON LEFT(zzzzsys_select_id, LENGTH(zzzzsys_select_id) - 3) = zzzzsys_form_id\n           WHERE zzzzsys_form_id  = ? \n	\";\n\n    $t = nuRunQuery($s, [$f1]);\n\n    while ($row = db_fetch_array($t)) {\n\n        $row[\'ssc_zzzzsys_select_id\'] = lookupPk($formSelectPks, $row[\'ssc_zzzzsys_select_id\']);\n        $row[\'zzzzsys_select_clause_id\'] = getPk($row[\'zzzzsys_select_clause_id\']);\n        if ($row[\'ssc_zzzzsys_select_id\'] != \"\") writeRecord(\'zzzzsys_select_clause\', $row, $first, \'\');\n\n    }\n\n}\n\nfunction cloneObjectsSelect($f1, $objectPks, array &$selectPks) {\n\n    $s = \"\n        SELECT\n           zzzzsys_select.* \n        FROM\n           zzzzsys_select \n           LEFT JOIN\n              zzzzsys_object \n              ON zzzzsys_object_id = LEFT(zzzzsys_select_id, LENGTH(zzzzsys_select_id) - 3) \n        WHERE\n           sob_all_zzzzsys_form_id = ?\n	\";\n\n    $t = nuRunQuery($s, [$f1]);\n\n    while ($row = db_fetch_array($t)) {\n\n        $event = eventFromPk($row[\'zzzzsys_select_id\']);\n        $newPk = lookupPk($objectPks, pkWithoutEvent($row[\'zzzzsys_select_id\'])) . $event;\n        addToArray($selectPks, $row[\'zzzzsys_select_id\'], $newPk);\n        $row[\'zzzzsys_select_id\'] = $newPk;\n\n        writeRecord(\'zzzzsys_select\', $row, $first, \'\');\n\n    }\n\n}\n\nfunction cloneObjectsSelectClause($f1, $selectPks) {\n\n    $s = \"\n        SELECT\n           zzzzsys_select_clause.* \n        FROM\n           zzzzsys_select_clause \n           LEFT JOIN\n              zzzzsys_select \n              ON zzzzsys_select_id = ssc_zzzzsys_select_id \n           LEFT JOIN\n              zzzzsys_object \n              ON zzzzsys_object_id = LEFT(zzzzsys_select_id, LENGTH(zzzzsys_select_id) - 3) \n        WHERE\n           sob_all_zzzzsys_form_id = ?\n	\";\n\n    $t = nuRunQuery($s, [$f1]);\n\n    while ($row = db_fetch_array($t)) {\n\n        $row[\'ssc_zzzzsys_select_id\'] = lookupPk($selectPks, $row[\'ssc_zzzzsys_select_id\']);\n        $row[\'zzzzsys_select_clause_id\'] = getPk($row[\'zzzzsys_select_clause_id\']);\n\n        if ($row[\'ssc_zzzzsys_select_id\'] != \"\") writeRecord(\'zzzzsys_select_clause\', $row, $first, \'\');\n\n    }\n\n}\n\nfunction cloneObjectsEvents($f1, $objectPks) {\n\n    $s = \"\n        SELECT\n            *\n        FROM\n            zzzzsys_event\n        WHERE\n            sev_zzzzsys_object_id IN (\n            SELECT\n                zzzzsys_object_id\n            FROM\n                zzzzsys_object\n            WHERE\n                sob_all_zzzzsys_form_id = ?\n        )\n    \";\n    $t = nuRunQuery($s, [$f1]);\n\n    while ($row = db_fetch_array($t)) {\n\n        $row[\'zzzzsys_event_id\'] = getPk($row[\'zzzzsys_event_id\']);\n        $row[\'sev_zzzzsys_object_id\'] = lookupPk($objectPks, $row[\'sev_zzzzsys_object_id\']);\n\n        writeRecord(\'zzzzsys_event\', $row, $first, \'\');\n\n    }\n\n}\n\nfunction getOpenForm($f2) {\n\n    $ft = getFormType($f2);\n    $r = $ft == \'browseedit\' ? \"\" : \"-1\";\n\n    $code = getFormInfo($f2) [\"code\"];\n\n    $msg = \"\n	   var buttons = \' <button onclick=\\\"$(\\\'#nuMessageDiv\\\').remove();nuForm(\\\'$f2\\\', \\\'$r\\\', \\\'\\\', \\\'\\\', \\\'2\\\');\\\" class=\\\"nuActionButton\\\">Open Form</button>\';\n	   nuMessage([\'<h2>Cloning complete.</h2>Code: $code\' + buttons]);\n	   console.log(\'Cloning complete. Form Code: $code\');\n   \";\n\n    return $msg;\n    \n}\n\nfunction clearHashCookies() {\n\n    return;\n    \"\n        function clearHashCookies() {\n            nuSetProperty(\'cloner_form_source\',\'\');\n            nuSetProperty(\'cloner_form_dest\',\'\');\n            nuSetProperty(\'cloner_tabs\',\'\');\n            nuSetProperty(\'cloner_objects\', \'1\');\n            nuSetProperty(\'cloner_subforms\', \'0\');\n            nuSetProperty(\'cloner_iframe_forms\', \'0\');\n            nuSetProperty(\'cloner_dump\',\'0\');\n            nuSetProperty(\'cloner_new_pks\',\'1\');\n            nuSetProperty(\'cloner_replace_into\',\'0\');\n        }\n        \n        clearHashCookies();\n    \";\n\n}\n\nfunction showError($msg) {\n    \n    nuJavaScriptCallback(\"nuMessage([\'<h2>Error</h2><br>\" . $msg . \"\']);\" . clearHashCookies());\n    \n}\n\nfunction showForm($f2, $dump) {\n\n    if ($dump == \'1\') return;\n    nuJavaScriptCallback(getOpenForm($f2) . clearHashCookies());\n\n}\n\nfunction whereSubforms() {\n    \n    $subforms = getSubformList();\n    return $subforms != \'\' ? \" AND sob_all_id IN ($subforms) \" : \"\";\n    \n}\n\nfunction whereRunIframeforms() {\n    \n    $forms = getIframeFormList();\n    return $forms != \'\' ? \" AND sob_all_id IN ($forms) \" : \"\";\n    \n}\n\nfunction updateObjectSubform($f1, $f2, $dump) {\n\n   $s = \"UPDATE zzzzsys_object SET sob_subform_zzzzsys_form_id = \'$f2\' WHERE sob_subform_zzzzsys_form_id = \'$f1\';\";\n   \n   if ($dump == \"1\") {\n       echoHeader(\'zzzzsys_object: UPDATE subform ID\');\n       echoPlainText($s);\n   } else {\n        $t = nuRunQuery($s);\n   }\n    \n}\n\nfunction updateIframeForm($f1, $f2, $dump) {\n\n   $s = \"UPDATE zzzzsys_object SET sob_run_zzzzsys_form_id = \'$f2\' WHERE sob_run_zzzzsys_form_id = \'$f1\';\";\n   \n   if ($dump == \"1\") {\n       echoHeader(\'zzzzsys_object: UPDATE Run iframe form\');\n       echoPlainText($s);\n   } else {\n        $t = nuRunQuery($s);\n   }\n    \n}\n\n\nfunction processIframeForms($f1, $tabPks, $dump) {\n\n    if (\"#cloner_iframe_forms#\" == \'0\') return;\n\n    $s = \"\n        SELECT\n            `sob_run_zzzzsys_form_id`\n        FROM\n            zzzzsys_object\n        WHERE\n            sob_all_zzzzsys_form_id = ? AND \n            sob_all_type = ? AND \n            sob_run_method = ? AND \n            IFNULL(sob_run_zzzzsys_form_id, \'\') <> \'\'\n        \";\n        \n    $s .= whereRunIframeforms();\n     \n    $t = nuRunQuery($s, [$f1,\'Run\',\'i\']);\n\n    while ($row = db_fetch_array($t)) {\n            \n           $f1 = $row[\'sob_run_zzzzsys_form_id\'];\n           \n           processForm($f1, $f2, $tabPks);\n           updateIframeForm($row[\'sob_run_zzzzsys_form_id\'], $f2, $dump);\n           processObjects($f1, $f2, $tabPks);\n           \n           $f2 = \"\";\n\n    }\n\n}\n\nfunction processSubforms($f1, $tabPks, $dump) {\n\n    if (\"#cloner_subforms#\" == \'0\') return;\n\n    $s = \"\n         SELECT \n            sob_subform_zzzzsys_form_id \n         FROM zzzzsys_object \n         WHERE sob_all_zzzzsys_form_id = ? AND sob_all_type = ?\n        \".whereSubforms();\n\n    $t = nuRunQuery($s, [$f1,\'subform\']);\n    while ($row = db_fetch_array($t)) {\n            \n           $f1 = $row[\'sob_subform_zzzzsys_form_id\'];\n\n           processForm($f1, $f2, $tabPks);\n           updateObjectSubform($row[\'sob_subform_zzzzsys_form_id\'], $f2, $dump);\n           processObjects($f1, $f2, $tabPks);\n           \n           $f2 = \"\";\n\n    }\n\n}\n\nfunction processForm($f1, &$f2, &$tabPks) {\n\n    if ($f2 != \"\") {\n        $tabPks = cloneFormTabs($f1, $f2, \'_clone\');\n        return;\n    }\n\n    $formSelectPks = [];\n    \n    $f2 = cloneForm($f1);\n    $tabPks = cloneFormTabs($f1, $f2);\n\n    cloneFormSelect($f1, $f2, $formSelectPks);\n    cloneFormSelectClause($f1, $formSelectPks);\n    cloneFormBrowse($f1, $f2);\n    cloneFormPHP($f1, $f2);\n\n}\n\nfunction processObjects($f1, $f2, &$tabPks) {\n\n    if (\"#cloner_objects#\" == \'0\') return;\n\n    $objectPks = [];\n    $selectPks = [];\n    \n    cloneObjects($f1, $f2, $objectPks, $tabPks);\n    cloneObjectsPHP($f1, $objectPks);\n    cloneObjectsSelect($f1, $objectPks, $selectPks);\n    cloneObjectsSelectClause($f1, $selectPks);\n    cloneObjectsEvents($f1, $objectPks);\n\n}\n\nfunction startCloner() {\n\n    $dump = \"#cloner_dump#\";\n\n	if(nuDemo(false) && $dump != \'1\'){\n\n		showError(\'Not available in the Demo...\');\n		return;\n\n	}\n	\n    $newPks = \"#cloner_new_pks#\";\n    if ($newPks == \'0\' && $dump != \'1\') {\n        showError(\'Primary keys can only be retained in dump mode.\');\n        return;\n    }\n\n    if (getFormSource($f1) == false) {\n        showError(\'The form $f1 (cloner_form_source) does not exist!\');\n        return;\n    }\n\n    if (getFormDestination($f2) == false) {\n        showError(\'The form $f2 (cloner_form_dest) does not exist!\');\n        return;\n    }\n\n\n\n    dumpFormInfo($f1, $dump);\n    processForm($f1, $f2, $tabPks);\n    processObjects($f1, $f2, $tabPks);\n    processSubforms($f1, $tabPks, $dump);\n    processIframeForms($f1, $tabPks, $dump);\n    \n    showForm($f2, $dump);\n\n}\n\nstartCloner();', NULL, NULL, 'hide', '', '1', '0', '0', '0', '', NULL),
