('nu683f01deaab6666', 'nu_inspect_record', 'Inspect a Record\'s Columns and Values', 'nubuilder', 'Debugging', '1', '$table = nuGetProperty(\'nu_inspect_record_table\');\n$record_id = nuGetProperty(\'nu_inspect_record_record_id\');\n\n$style = \"<style>\n.responsive-table {\n  overflow-x: auto;\n}\n\n.styled-table {\n  width: 100%;\n  border-collapse: collapse;\n  font-family: Arial, sans-serif;\n  margin-top: 1em;\n}\n\n.styled-table thead {\n  background-color: #f2f2f2;\n  cursor: pointer;\n}\n\n.styled-table th, .styled-table td {\n  border: 1px solid #ddd;\n  padding: 8px 12px;\n  user-select: text;\n}\n\n.styled-table tbody tr:nth-child(odd) {\n  background-color: #fafafa;\n}\n\n.styled-table tbody tr:nth-child(even) {\n  background-color: #f5f5f5;\n}\n\n.styled-table thead th {\n  position: sticky;\n  top: 0;\n  background-color: #f2f2f2;\n  z-index: 2;\n  user-select: all;\n}\n\ncode {\n  font-family: monospace;\n  padding: 2px 4px;\n  border-radius: 3px;\n}\n\n.filter-controls label {\n  user-select: none;\n}\n\n.filter-controls select {\n  user-select: none;\n  -webkit-user-select: none;\n  -moz-user-select: none;\n}\n\n.filter-controls select {\n  padding: 4px 8px;\n  font-size: 14px;\n}\n\n.sort-icon {\n  margin-left: 5px;\n  opacity: 0.6;\n}\n\n.copy-icon {\n  margin-left: 8px;\n  cursor: pointer;\n  color: #5a5a5a;\n}\n</style>\";\n\n$link = \'<link rel=\"stylesheet\" href=\"../third_party/fontawesome/css/all.min.css\">\';\n\n$script = <<<JS\n<script>\nfunction filterRows() {\n	const select = document.getElementById(\'columnFilter\');\n	const rows = document.querySelectorAll(\'.styled-table tbody tr\');\n	const mode = select.value;\n\n	rows.forEach(row => {\n		const valueCell = row.querySelector(\'td:nth-child(3)\');\n		const text = valueCell.textContent.trim();\n		const isEmpty = text === \'\' || text === \'NULL\';\n		const isNonEmpty = !isEmpty;\n\n		if (mode === \'all\') {\n			row.style.display = \'\';\n		} else if (mode === \'nonempty\' && isNonEmpty) {\n			row.style.display = \'\';\n		} else if (mode === \'empty\' && isEmpty) {\n			row.style.display = \'\';\n		} else {\n			row.style.display = \'none\';\n		}\n	});\n}\n\nlet currentSort = { column: -1, direction: \'\' };\nfunction sortTable(columnIndex) {\n	const table = document.querySelector(\'.styled-table tbody\');\n	const rows = Array.from(table.rows);\n	const headers = document.querySelectorAll(\'.styled-table thead th\');\n\n	headers.forEach((th, i) => {\n		const icon = th.querySelector(\'.sort-icon\');\n		if (icon) icon.className = \'fa-solid sort-icon\';\n	});\n\n	let direction = \'asc\';\n	if (currentSort.column === columnIndex && currentSort.direction === \'asc\') direction = \'desc\';\n\n	rows.sort((a, b) => {\n		let aText = a.cells[columnIndex].innerText.trim().toLowerCase();\n		let bText = b.cells[columnIndex].innerText.trim().toLowerCase();\n		return (direction === \'asc\') ? aText.localeCompare(bText) : bText.localeCompare(aText);\n	});\n\n	rows.forEach(row => table.appendChild(row));\n\n	const icon = headers[columnIndex].querySelector(\'.sort-icon\');\n	if (icon) icon.classList.add(direction === \'asc\' ? \'fa-caret-up\' : \'fa-caret-down\');\n\n	currentSort = { column: columnIndex, direction };\n}\n\nfunction copyToClipboard(icon) {\n	const value = icon.getAttribute(\'data-value\');\n	navigator.clipboard.writeText(value);\n\n	icon.classList.remove(\"fa-copy\");\n	icon.classList.add(\"fa-solid\",\"fa-check\");\n\n	setTimeout(() => {\n		icon.classList.remove(\"fa-solid\",\"fa-check\");\n		icon.classList.add(\"fa-copy\");\n	}, 550);\n}\n\n</script>\nJS;\n\n$columns_query = \"\n	SELECT COLUMN_NAME, COLUMN_TYPE, COLUMN_KEY, COLUMN_COMMENT\n	FROM INFORMATION_SCHEMA.COLUMNS\n	WHERE TABLE_SCHEMA = DATABASE()\n	  AND TABLE_NAME = \'$table\'\n	ORDER BY ORDINAL_POSITION\n\";\n\n$columns_result = nuRunQuery($columns_query);\n\n$fieldInfo = db_field_info($table);\n$primaryKeys = $fieldInfo[2] ?? [];\n$firstPrimaryKey = $primaryKeys[0] ?? null;\n\n$data_query = \"SELECT * FROM `$table` WHERE `$firstPrimaryKey` = \'$record_id\' LIMIT 1\";\n$data_result = nuRunQuery($data_query);\n$data_row = db_fetch_array($data_result);\n\necho \'<div class=\"responsive-table\">\';\necho $style . $link . $script;\n\necho \'<div class=\"filter-controls\">\';\necho \'<label for=\"columnFilter\"><strong>Show:</strong> </label>\';\necho \'<select id=\"columnFilter\" onchange=\"filterRows()\">\';\necho \'<option value=\"all\">All Columns</option>\';\necho \'<option value=\"nonempty\">Only Columns With a Value</option>\';\necho \'<option value=\"empty\">Only Empty/Null Columns</option>\';\necho \'</select>\';\necho \'</div>\';\n\necho \'<table class=\"styled-table\">\';\necho \'<thead><tr>\';\necho \'<th onclick=\"sortTable(0)\">Field <i class=\"fa-solid sort-icon\"></i></th>\';\necho \'<th onclick=\"sortTable(1)\">Type <i class=\"fa-solid sort-icon\"></i></th>\';\necho \'<th onclick=\"sortTable(2)\">Value <i class=\"fa-solid sort-icon\"></i></th>\';\necho \'</tr></thead>\';\necho \'<tbody>\';\n\nwhile ($col = db_fetch_array($columns_result)) {\n	$name = $col[\'COLUMN_NAME\'];\n	$type = $col[\'COLUMN_TYPE\'];\n	$key = $col[\'COLUMN_KEY\'];\n	$comment = $col[\'COLUMN_COMMENT\'];\n\n	$raw_value = $data_row[$name] ?? null;\n	$value = is_null($raw_value) ? \'<em>NULL</em>\' : htmlspecialchars($raw_value);\n\n	$icon = \'\';\n	$bg = \'\';\n	if ($key == \'PRI\') {\n		$icon = \' <i class=\"fa-solid fa-key\" style=\"color:goldenrod;\" title=\"Primary Key\"></i>\';\n		$bg = \' style=\"background-color:#fff8dc;\"\';\n	} elseif ($key == \'MUL\') {\n		$icon = \' <i class=\"fa-solid fa-key\" style=\"color:grey;\" title=\"Foreign Key\"></i>\';\n		$bg = \' style=\"background-color:#f5f5f5;\"\';\n	}\n\n	$raw_length = mb_strlen($raw_value ?? \'\');\n\n	if ($raw_value === null || $raw_length <= 200) {\n		$display_value = $value;\n		$tooltip = \'\';\n	} else {\n		$display_value = htmlspecialchars(mb_substr($raw_value, 0, 200)) . \'…\';\n\n		$tooltip_content = mb_substr($raw_value, 0, 2000);\n		if ($raw_length > 2000) {\n			$tooltip_content .= \'…\';\n		}\n\n		$tooltip = \' title=\"\' . htmlspecialchars($tooltip_content) . \'\"\';\n	}\n\n	$escaped_value = htmlspecialchars($raw_value ?? \'\', ENT_QUOTES);\n\n	if (is_null($raw_value) || $raw_value === \'\') {\n		$copy_icon = \'\';\n	} else {\n		$copy_icon = \"<i class=\'fa-regular fa-copy copy-icon\' title=\'Copy\' data-value=\'$escaped_value\' onclick=\'copyToClipboard(this)\'></i>\";\n	}\n\n	$field_title = $comment ? \' title=\"\' . htmlspecialchars($comment) . \'\"\' : \'\';\n\n	echo \"<tr>\";\n	echo \"<td$bg><strong$field_title>$name</strong>$icon</td>\";\n	echo \"<td$bg><code>$type</code></td>\";\n	echo \"<td$bg><code$tooltip>$display_value</code>$copy_icon</td>\";\n	echo \"</tr>\";\n}\n\necho \'</tbody></table></div>\';', NULL, NULL, 'window', NULL, '1', '0', '1', '0', '', NULL),
('nuitem_BB', 'nuitem_BB', 'System PHP', 'nubuilder', NULL, '1', '// Temp table\n\n$denied = $GLOBALS[\'nuSetup\']->set_denied == 1;\n\n$condition = $denied\n    ? \"(zzzzsys_item_id NOT LIKE \'nu%\')\"\n    : \"1=1\";\n\n$sql = \"\n    SELECT\n        zzzzsys_item_id,\n        itm_value,\n        itm_description,\n        itm_category,\n        itm_group,\n        itm_tag,\n        itm_language,\n        sob_all_id,\n        sfo_code,\n        itm_active,\n        itm_created_on,\n        itm_updated_on\n    FROM\n        zzzzsys_item\n    LEFT JOIN zzzzsys_object ON zzzzsys_object_id = itm_object_id\n    LEFT JOIN zzzzsys_form ON zzzzsys_form_id = zzzzsys_object.sob_all_zzzzsys_form_id\n    WHERE\n        $condition\n    ORDER BY\n        sfo_code ASC,\n        sob_all_id ASC\n\";\n\n\nnuCreateTableFromSelect(\'#TABLE_ID#\', $sql);\n\n\n// Filter\n$getDistinctGroupColumnQuery = function($column, $condition) {\n    return \"\n        SELECT DISTINCT TRIM(`$column`) AS `value`, TRIM(`$column`) AS `text`\n        FROM zzzzsys_item\n        WHERE \n            IFNULL(itm_group, \'\') <> \'\' \n            AND ($condition)\n        ORDER BY `value`\n    \";\n};\n\n$sqlGroup = function($condition) use ($getDistinctGroupColumnQuery) {\n    return $getDistinctGroupColumnQuery(\'itm_group\', $condition);\n};\n\n$group = nuEncodeQueryRowResults($sqlGroup($condition), [], [\'-1\',\'\']);\n\n$filterJS = \"\n    function nuItemsFilterGetData(data) {\n        return JSON.parse(atob(data));\n    }\n    function nuItemsFilterGetGroup() {\n        return nuItemsFilterGetData(\'$group\');\n    }\n\";\n\nnuAddJavaScript($filterJS);', NULL, NULL, NULL, NULL, '1', '0', '1', '0', NULL, NULL),
('nuitem_AS', 'nuitem_AS', 'System PHP', 'nubuilder', NULL, '1', '// Don\'t store itm_created_on and itm_updated_on for nu-records:\n\n// Get the current record ID\n$recordID = \'#RECORD_ID#\';\n\n// Check if it starts with \'nu\'\nif (strpos($recordID, \'nu\') === 0) {\n    \n    // Prepare the SQL update\n    $sql = \"\n        UPDATE zzzzsys_item \n        SET itm_created_on = NULL, \n            itm_updated_on = NULL \n        WHERE zzzzsys_item_id = ?\n    \";\n\n    // Execute the query with parameter binding\n\n    nuRunQuery($sql, [$recordID]);\n}\n', NULL, NULL, NULL, NULL, '1', '0', '1', '0', NULL, NULL),
('nu683830320e3343b', 'nu_ai_prompt_generator_test_credentials', 'Testing AI Credentials', 'nubuilder', 'Generator', '1', 'include_once \'nuai.php\';\n\n\n$result = nuAITestCredentials();\n\nif ($result[\'success\']) {\n    // Credentials are good\n    echo $result[\'message\'];            // e.g. “Credentials are valid!”\n} else {\n    // Something went wrong\n    // You can log full details if you like:\n   // error_log(\"OpenAI test failed ({$result[\'http_code\']}) Raw response: {$result[\'raw\']}\");\n    \n    // And display a user‐friendly message:\n    echo \"Error testing credentials: \" . $result[\'message\'];\n}', NULL, NULL, 'window', NULL, '1', '0', '1', '0', '', NULL),
('nu68394ef10bf2cc9', 'nu_ai_prompt_generator_call_api', 'AI Prompt Generator ', 'nubuilder', 'Generator', '1', 'include_once \'nuai.php\';\n\n// 1. Fetch the raw JSON params\n$paramsJson = nuGetProperty(\'nuaipromptgenerator_params\');\nif (empty($paramsJson)) {\n    return; // nothing to do\n}\n\n// 2. Decode\n$params       = nuDecode($paramsJson);\n$procedure    = $params[\'procedure_type\'] ?? null;\n$buttonId     = $params[\'buttonId\']       ?? null;\n\n// 3. Dispatch based on procedure_type\nswitch ($procedure) {\n    case \'response\':\n        $response     = nuAIPromptGetResponse($params);\n        $callbackName = \'nuGeneratorResponseExecuted\';\n        break;\n\n    case \'tag\':\n        $response     = nuAIPromptGetTagsFromPrompt($params);\n        $callbackName = \'nuGeneratorExecutedTags\';\n        break;\n\n    // --- add new cases here ---\n    // case \'something_else\':\n    //     $response     = nuAIPromptDoSomethingElse($params);\n    //     $callbackName = \'nuGeneratorSomethingElseExecuted\';\n    //     break;\n\n    default:\n        // unknown procedure — return or fire a generic error callback\n        $errorMsg = json_encode(\"Unknown procedure: {$procedure}\");\n        nuJavaScriptCallback(\"(function(){ console.error({$errorMsg}); })();\");\n        return;\n}\n\n// 4. Determine error vs. success\n$isError = ! empty($response[\'error\']);\n$payload = $isError\n    ? $response[\'message\']\n    : $response[\'result\'];\n\n// 5. JSON-encode for JS\n$buttonJs = json_encode($buttonId, JSON_UNESCAPED_SLASHES | JSON_UNESCAPED_UNICODE);\n$dataJs   = json_encode($payload,  JSON_UNESCAPED_SLASHES | JSON_UNESCAPED_UNICODE);\n\n// 6. Build & fire the callback\n$js = sprintf(\n    \"(function(){%s(%s, %s, %s);})();\",\n    $callbackName,\n    $buttonJs,\n    $dataJs,\n    $isError ? \'false\' : \'true\'\n);\n\nnuJavaScriptCallback($js);\n', NULL, NULL, 'hide', '', '1', '0', '1', '0', '', NULL),
('nu68397bb77c048bb_BB', 'nu68397bb77c048bb_BB', 'System PHP', 'nubuilder', NULL, '1', '$denied = $GLOBALS[\'nuSetup\']->set_denied == 1;\n\n$condition = $denied\n    ? \"(zzzzsys_item_id NOT LIKE \'nu%\')\"\n    : \"1=1\";\n\n$sql = \"\n    SELECT\n        zzzzsys_item_id,\n        itm_value,\n        itm_description,\n        itm_category,\n        itm_object_id,\n        itm_tag,\n        itm_language,\n        sob_all_id,\n        sfo_code,\n        itm_active,\n        itm_created_on,\n        itm_updated_on,\n        itm_sort_order,\n        sob_all_zzzzsys_form_id\n    FROM\n        zzzzsys_item\n    LEFT JOIN zzzzsys_object ON zzzzsys_object_id = itm_object_id\n    LEFT JOIN zzzzsys_form ON zzzzsys_form_id = zzzzsys_object.sob_all_zzzzsys_form_id\n    WHERE\n        $condition\n    ORDER BY\n        sfo_code ASC,\n        sob_all_id ASC,\n        itm_value ASC\n\";\n\n\nnuCreateTableFromSelect(\'#TABLE_ID#\', $sql);', NULL, NULL, NULL, NULL, '1', '0', '1', '0', NULL, NULL),
('nu683fd76e8963821', 'nu_run_alter_db_column', 'Alter a database column', 'nubuilder', 'Database', '1', 'if (nuHash()[\'GLOBAL_ACCESS\'] == \'0\') return;\n\n$sql = \'ALTER TABLE `#sob_all_table#` CHANGE `#sob_all_id#` \' . \'#sql_query#\';\n\nif (preg_match(\'/DELETE |DROP |INSERT |;/\', strtoupper($sql))) {\n    $r = -2;\n} else {\n    $r = nuRunQuery($sql, [], true);\n}\n\nif ($r == 0) {\n    nuSetJSONDataAll(\'REFRESH_CACHE\', \'1\');\n    $js = \"$.nuInfoBar.success(nuTranslate(\'The column has been altered successfully\'));\n           parent.nuUpdateMessage(\'refresh_required\');\";\n} else if ($r == -1) {\n    $js = \"$.nuInfoBar.error(nuTranslate(\'An error occurred while altering the column\') + \'. \' + nuTranslate(\'Check nuDebug Results for details\'));\";\n} else if ($r == -2) {\n    $js = \"$.nuInfoBar.error(nuTranslate(\'An error occurred while altering the column\') + \'. \' + nuTranslate(\'The query contains invalid keywords\'));\";\n}\n\nnuJavaScriptCallback($js);', NULL, NULL, 'hide', '', '1', '0', '0', '0', '', NULL),
('nu5fdb9ffe0330e_AB', 'nu5fdb9ffe0330e_AB', 'System PHP', 'nubuilder', NULL, '1', '$hash = nuHash();\n$type = $hash[\'sfo_type\'] ?? \'\';\n\nif ($type !== \'launch\') {\n\n    nuSetFormValue(\'slf_add_button\', \'1\');\n    nuSetFormValue(\'slf_print_button\', \'1\');\n\n    if ($type !== \'browse\') {\n        nuSetFormValue(\'slf_save_button\', \'1\');\n        nuSetFormValue(\'slf_clone_button\', \'1\');\n        nuSetFormValue(\'slf_delete_button\', \'1\');\n    }\n\n}', NULL, NULL, NULL, NULL, '1', '0', '1', '0', NULL, NULL),
('nu6866e2a9c6afd1c', 'nu_upload_file_load_uppy_code', 'Get Uppy html code', 'nubuilder', 'File Upload', NULL, 'function getNuRunFileUppyHtmlSourceCombined() {\n\n    $codes = [\'nu_upload_file_uppy_code_template\',\n        \'nu_upload_file_uppy_code\'];\n    foreach ($codes as $code) {\n        $source = db_fetch_value(\n            \'zzzzsys_code_snippet\',\n            \'cot_code\',\n            $code,\n            \'cot_source_code\'\n        );\n        if ($source) {\n            return $source;\n        }\n    }\n\n    return false;\n\n}\n\n$source = getNuRunFileUppyHtmlSourceCombined();\nif ($source !== false) {\n    $source = base64_encode(json_encode($source));\n    $js = \"let htmlCode = $(\'#sob_html_code\');\n        nuSetValue(\'sob_html_code\', htmlCode.val() + JSON.parse(atob(\'$source\'))); \";\n\n    nuJavaScriptCallback($js);\n}', NULL, NULL, 'window', NULL, '1', '0', '1', '0', '', NULL),
('nuemailtemplate_BB', 'nuemailtemplate_BB', 'System PHP', 'nubuilder', NULL, NULL, '$getDistinctColumnQuery = function(string $column): string {\n    return \"\n        SELECT\n          DISTINCT TRIM({$column}) AS trimmed_col\n        FROM\n          zzzzsys_email_template\n          LEFT JOIN zzzzsys_form\n            ON zzzzsys_form_id = emt_form_id\n        WHERE\n          IFNULL({$column}, \'\') <> \'\'\n        ORDER BY\n          trimmed_col\n    \";\n};\n\n$sqlGroup = function() use ($getDistinctColumnQuery): string {\n    return $getDistinctColumnQuery(\'emt_group\');\n};\n$sqlCategory = function() use ($getDistinctColumnQuery): string {\n    return $getDistinctColumnQuery(\'emt_category\');\n};\n\n$group    = nuEncodeQueryRowResults($sqlGroup(),    []);\n$category = nuEncodeQueryRowResults($sqlCategory(), []);\n\n$filterJS = <<<JS\nfunction nuEmailTemplateFilterGetData(data) {\n    return JSON.parse(atob(data));\n}\nfunction nuEmailTemplateFilterGetGroup() {\n    return nuEmailTemplateFilterGetData(\'$group\');\n}\nfunction nuEmailTemplateFilterGetCategory() {\n    return nuEmailTemplateFilterGetData(\'$category\');\n}\nJS;\n\nnuAddJavaScript($filterJS);\n', NULL, NULL, NULL, NULL, '1', '0', '1', '0', NULL, NULL),
('nu6881aa082e6e173', 'nu_form_get_php', 'nubuilder', 'nubuilder', 'Dev', NULL, 'function nuFormGetPHP($record_id) {\n    return db_fetch_value(\'zzzzsys_php\', \'zzzzsys_php_id\', $record_id, \'sph_php\');\n}\n\n$val = nuEncode(nuFormGetPHP(\'nuuser_BB\'));\n$js = \"nuSetValue(\'nu-code-textarea-php-bb\', nuDecode(\'$val\'));\";\n\nnuJavaScriptCallback($js);\n\n\nnuDebug(nuEncode(\'1\'));', NULL, NULL, NULL, NULL, '1', '0', '1', '0', '', NULL),
('nuappcloner', 'nu_app_cloner', 'nuBuilder Application Cloner', 'nubuilder', 'Cloner', NULL, 'include_once \'nuappcloner.php\';\n\n// Get parameters from the form\n$params = nuGetProperty(\'nu_app_cloner_params\');\nif (!$params) {\n    nuAppClonerCallback(\'Error\', \'Failed to retrieve parameters\');\n    return;\n}\n\n$params = nuDecode($params);\n\n// Extract all parameters\n$targetHost = $params[\'targetHost\'] ?? \'\';\n$targetDB = $params[\'targetDB\'] ?? \'\';\n$targetUsername = $params[\'targetUsername\'] ?? \'\';\n$targetPassword = $params[\'targetPassword\'] ?? \'\';\n$targetCharset = $params[\'targetCharset\'] ?? \'utf8mb4\';\n$targetPort = intval($params[\'targetPort\'] ?? 3306);\n$sourcePath = realpath($params[\'sourcePath\'] ?? \'..\');\n$targetPath = $params[\'targetPath\'] ?? \'\';\n$insertType = $params[\'insertType\'] ?? \'INSERT\';\n$opts = $params[\'opts\'] ?? [];\n$config = $params[\'config\'] ?? [];\n\n// Convert string \"true\"/\"false\" to boolean if needed\nforeach ($config as $key => $value) {\n    if ($value === \'true\') $config[$key] = true;\n    if ($value === \'false\') $config[$key] = false;\n}\n\n\n// Create cloner instance\n$cloner = new nuBuilderCloner($config);\n\n// Run the clone operation\n$success = $cloner->cloneDatabase(\n    $targetDB,\n    $targetHost,\n    $targetUsername,\n    $targetPassword,\n    $targetCharset,\n    $targetPort,\n    $opts,\n    $insertType,\n    $sourcePath,\n    $targetPath\n);\n\nfunction nuAppClonerCallback(string $type, string $message): void {\n    nuJavaScriptCallback(\"nuMessage(\'$type\', \'\" . addslashes($message) . \"\');\");\n}', 'function nuOnProcedureRun() {\n    nuSetProperty(\'nu_app_cloner_params\',\'xx\');\n}', '1', 'hide', NULL, '1', '0', '1', '0', '', NULL),
('nuappcloner_BE', 'nuappcloner_BE', 'System PHP', 'nubuilder', NULL, NULL, 'if (nuDemo()) return;\n\nglobal $nuConfigDBUser;\nglobal $nuConfigDBPassword;\n\n$js = \"\n    	if (nuIsNewRecord()) { debugger;\n            nuSetValue(\'cla_target_username\', \'$nuConfigDBUser\'); \n            nuSetValue(\'cla_target_password\', \'$nuConfigDBPassword\'); \n        };\n\";\n\nnuAddJavaScript($js);', NULL, NULL, NULL, NULL, '1', '0', '1', '0', NULL, NULL);
