('nuform_BS', 'nuform_BS', 'System PHP', 'nubuilder', '\n    $o = \'#sfo_type#\';\n\n    if(\'#sfo_type#\'             == \'\'){nuDisplayError(\'<b>Type</b> Cannot Be Blank..\', \'sfo_type\');}\n    if(\'#sfo_code#\'             == \'\'){nuDisplayError(\'<b>Code</b> Cannot Be Blank..\', \'sfo_code\');}\n    if(\'#sfo_description#\'      == \'\'){nuDisplayError(\'<b>Description</b> Cannot Be Blank..\', \'sfo_description\');}\n\n\n    \n    \n    if($o == \'browseedit\' || $o == \'subform\' || $o == \'browse\'){\n        \n        if($o != \'browse\'){\n            nuCheckTabs();\n        }\n        \n        if($o != \'subform\'){\n            nuCheckBrowse();\n        }\n        \n        if(\'#sfo_browse_sql#\'   == \'\'){nuDisplayError(\'<b>Browse SQL</b> Cannot Be Blank..\', \'sfo_browse_sql\');}\n    }\n\n    if($o == \'edit\' or $o == \'launch\'){\n        nuCheckTabs();\n    }\n\n    if($o != \'launch\'){\n        if(\'#sfo_table#\'            == \'\'){nuDisplayError(\'<b>Table Name</b> Cannot Be Blank..\', \'sfo_table\');}\n        if(\'#sfo_primary_key#\'      == \'\'){nuDisplayError(\'<b>Primary Key</b> Cannot Be Blank..\', \'sfo_primary_key\');}\n    }\n\nfunction nuCheckBrowse(){\n\n    $r  = 0;\n    $sf = nuSubformObject(\'zzzzsys_browse_sf\');\n    \n    for($i = 0 ; $i < count($sf->rows) ; $i++){\n       if($sf->deleted[$i] == 0){$r++;}\n    }\n    \n    if($r == 0){\n       nuDisplayError(\'<b>Must have at least 1</b> Browse Column Defined..\');\n    }\n    \n}\n\nfunction nuCheckTabs(){\n\n    $r  = 0;\n    $sf = nuSubformObject(\'zzzzsys_tab_sf\');\n    \n    for($i = 0 ; $i < count($sf->rows) ; $i++){\n       if($sf->deleted[$i] == 0){$r++;}\n    }\n    \n    if($r == 0){\n       nuDisplayError(\'<b>Must have at least 1</b> Tab Column Defined..\');\n    }\n    \n}\n\n\n\n\n', '', '', '1', ''),
('nupassword_BS', 'nupassword_BS', 'System PHP', 'nubuilder', '\n$is		= md5(\'#old_password#\');\n$was	= md5(\'#new_password_check#\');\n$will	= md5(\'#new_password#\');\n$ses	= \'#SESSION_ID#\';\n\n$s      = \"\n\n    SELECT * \n    FROM zzzzsys_session\n    WHERE zzzzsys_session_id = \'$ses\'\n\n\";\n\n$t  = nuRunQuery($s);\n$r  = db_fetch_object($t);\n$j  = json_decode($r->sss_access);\n\n$s      = \"\n\n    SELECT * \n    FROM zzzzsys_user\n    WHERE zzzzsys_user_id = \'#USER_ID#\'\n    AND sus_login_password = \'$is\'\n\n\";\n\n$t  = nuRunQuery($s);\n\nif(db_num_rows($t) == 0){\n\n    nuDisplayError(\'Incorrect Password\');\n    return;\n    \n}\n\n//-- added by toms\n\n$p	= nuProcedure(\'nuCheckPasswordPolicy\');\n\nif($p != \'\'){\n\n	eval($p);\n	\n	if($check == false){\n		return;\n	}\n\n}\n\n//--\n\nif($will == $was){\n\n    $s  = \"\n\n    UPDATE zzzzsys_user \n    SET sus_login_password = \'$will\'\n    WHERE zzzzsys_user_id = \'#RECORD_ID#\'\n    AND sus_login_password = \'$is\'\n    \n    \";\n\n    nuRunQuery($s);\n\n}else{\n    nuDisplayError(\'\"Enter New Password\" Must be the same as \"Re-Enter New Password\"\');\n}\n\n\n\n\n\n\n\n', '', '', '1', ''),
('nuuser_AS', 'nuuser_AS', 'System PHP', 'nubuilder', '\nif(\'#check_password#\' != \'\'){\n\n	$pw	= md5(\'#check_password#\');\n	nuRunQuery(\"UPDATE zzzzsys_user SET sus_login_password = \'$pw\' WHERE zzzzsys_user_id = \'#RECORD_ID#\'\");\n\n}\n\n', '', '', '1', ''),
('nudebug_BD', 'nudebug_BD', 'System PHP', 'nubuilder', '\n$i  = \'#instruction#\';\n\nif($i == \'all\'){\n    nuRunQuery(\"DELETE FROM zzzzsys_debug\");\n    \n}\n\n', '', '', '1', ''),
('nuobject_BS', 'nuobject_BS', 'System PHP', 'nubuilder', '\n    $r  = \'#RECORD_ID#\';\n    $f  = \'#sob_all_zzzzsys_form_id#\';\n    $o  = \'#sob_all_id#\';\n\n    $s  = \"\n    \n        SELECT COUNT(*) \n        FROM zzzzsys_object\n        WHERE sob_all_zzzzsys_form_id = \'$f\'\n        AND sob_all_id = \'$o\'\n        AND zzzzsys_object_id != \'$r\'\n\n        \";\n\n    $t  = nuRunQuery($s);\n    $r  = db_fetch_row($t);\n    \n    if($r[0] > 0){\n        nuDisplayError(\'This <b>ID</b> is already used on this Form..\');\n    }\n\n    $o = \'#sob_all_type#\';\n\n    if(\'#sob_all_zzzzsys_tab_id#\'   == \'\'){nuDisplayError(\'Type Cannot Be Blank..\', \'sob_all_zzzzsys_tab_idcode\');}\n    if(\'#sob_all_type#\'             == \'\'){nuDisplayError(\'Type Cannot Be Blank..\', \'sob_all_type\');}\n    if(\'#sob_all_zzzzsys_form_id#\'  == \'\'){nuDisplayError(\'Tab - Form Cannot Be Blank..\', \'sob_all_zzzzsys_form_idcode\');}\n    if(\'#sob_all_id#\'               == \'\'){nuDisplayError(\'ID Cannot Be Blank..\', \'sob_all_id\');}\n    if(\'#sob_all_label#\'            == \'\'){nuDisplayError(\'Label Cannot Be Blank..\', \'sob_all_label\');}\n    if(\'#sob_all_top#\'              == \'\'){nuDisplayError(\'Top Cannot Be Blank..\', \'sob_all_top\');}\n    if(\'#sob_all_left#\'             == \'\'){nuDisplayError(\'Left Cannot Be Blank..\', \'sob_all_left\');}\n    if(\'#sob_all_width#\'            == \'\'){nuDisplayError(\'Width Cannot Be Blank..\', \'sob_all_width\');}\n    if(\'#sob_all_height#\'           == \'\'){nuDisplayError(\'Height Cannot Be Blank..\', \'sob_all_height\');}\n    if(\'#sob_all_cloneable#\'        == \'\'){nuDisplayError(\'Cloneable Cannot Be Blank..\', \'sob_all_cloneable\');}\n    if(\'#sob_all_align#\'            == \'\'){nuDisplayError(\'Align Cannot Be Blank..\', \'sob_all_align\');}\n    if(\'#sob_all_validate#\'         == \'\'){nuDisplayError(\'Validation Cannot Be Blank..\', \'sob_all_validate\');}\n    if(\'#sob_all_access#\'           == \'\'){nuDisplayError(\'Read Only Cannot Be Blank..\', \'sob_all_access\');}\n\n\n    if($o == \'display\'){\n        if(\'#sob_display_sql#\'   == \'\'){nuDisplayError(\'Display Cannot Be Blank..\',\'sob_display_sql\');}\n    }\n\n    if($o == \'html\'){\n        \n        if(\'#sob_html_code#\'   == \'\' && \'#sob_html_chart_type#\'   == \'\'){nuDisplayError(\'HTML Fields Cannot Both Be Blank..\',\'sob_html_code\');}\n        if(\'#sob_html_javascript#\'   == \'\' && \'#sob_html_chart_type#\'   != \'\'){nuDisplayError(\'HTML Javascript Array Cannot Be Blank..\',\'sob_html_javascript\');}\n        \n    }\n    \n    \n\n    if($o == \'lookup\'){\n        if(\'#sob_lookup_zzzzsys_form_id#\'   == \'\'){nuDisplayError(\'Form Cannot Be Blank..\', \'sob_lookup_zzzzsys_form_idcode\');}\n        if(\'#sob_lookup_code#\'   == \'\'){nuDisplayError(\'Code Cannot Be Blank..\', \'sob_lookup_code\');}\n        if(\'#sob_lookup_description#\'   == \'\'){nuDisplayError(\'Descrition Cannot Be Blank..\', \'sob_lookup_description\');}\n        if(\'#sob_lookup_description_width#\'   == \'\'){nuDisplayError(\'Width Cannot Be Blank..\', \'sob_lookup_description_width\');}\n    }\n\n    if($o == \'run\'){\n        if(\'#sob_run_zzzzsys_form_id#\'   == \'\'){nuDisplayError(\'Run Cannot Be Blank..\', \'sob_run_zzzzsys_form_idcode\');}\n        if(\'#sob_run_method#\'   == \'\'){nuDisplayError(\'Method Cannot Be Blank..\', \'sob_run_method\');}\n    }\n\n\n    if($o == \'input\'){\n        \n        if(\'#sob_input_type#\'   == \'\'){nuDisplayError(\"Input\'s Input Type Cannot Be Blank..\", \'sob_input_type\');}\n        \n        if(\'#sob_input_type#\' == \'nuNumber\' || \'#sob_input_type#\' == \'nuDate\'){\n            if(\'#sob_input_format#\' == \'\'){nuDisplayError(\"Input Format Cannot Be Blank..\", \'sob_input_format\');}\n        }\n        \n        if(\'#sob_input_type#\' == \'nuAutoNumber\'){\n            if(\'#sob_input_count#\' == \'\'){nuDisplayError(\"Next Number Cannot Be Blank..\", \'sob_input_format\');}\n        }\n        \n    }\n\n\n    if($o == \'select\'){\n        if(\'#sob_select_multiple#\'   == \'\'){nuDisplayError(\'Multiple Cannot Be Blank..\', \'sob_select_multiple\');}\n        if(\'#sob_select_sql#\'   == \'\'){nuDisplayError(\'SQL/List Cannot Be Blank..\', \'sob_select_sql\');}\n    }\n    \n\n    if($o == \'subform\'){\n        if(\'#sob_subform_zzzzsys_form_id#\'   == \'\'){nuDisplayError(\'Form Cannot Be Blank..\', \'sob_subform_zzzzsys_form_idcode\');}\n        if(\'#sob_subform_foreign_key#\'   == \'\'){nuDisplayError(\'Foreign Key Cannot Be Blank..\', \'sob_subform_foreign_key\');}\n        if(\'#sob_subform_add#\'   == \'\'){nuDisplayError(\'Addable Cannot Be Blank..\', \'sob_subform_add\');}\n        if(\'#sob_subform_delete#\'   == \'\'){nuDisplayError(\'Deletable Cannot Be Blank..\', \'sob_subform_delete\');}\n        if(\'#sob_subform_type#\'   == \'\'){nuDisplayError(\'Type Cannot Be Blank..\', \'sob_subform_type\');}\n    }\n\n    if($o == \'\'){\n        if(\'#sfo_browse_sql#\'   == \'\'){nuDisplayError(\'Browse SQL Cannot Be Blank..\', \'sfo_browse_sql\');}\n    }\n', '', '', '1', ''),
('nuform_AS', 'nuform_AS', 'System PHP', 'nubuilder', '$s  = \"\n    SELECT * \n    FROM zzzzsys_browse \n    WHERE sbr_zzzzsys_form_id = \'#RECORD_ID#\'\n    ORDER BY sbr_order;\n\";\n\n\n$t = nuRunQuery($s);\n$o = 10;\n\nwhile($r = db_fetch_object($t)){\n        \n    $s  = \"\n        UPDATE zzzzsys_browse \n        SET sbr_order = \'$o\'\n        WHERE zzzzsys_browse_id = \'$r->zzzzsys_browse_id\'\n        ORDER BY sbr_order;\n    \";\n    \n    nuRunQuery($s);\n    \n    $o = $o + 10;    \n    \n}\n\n$s  = \"\n    SELECT * \n    FROM zzzzsys_tab \n    WHERE syt_zzzzsys_form_id = \'#RECORD_ID#\'\n    ORDER BY syt_order;\n\";\n\n$t = nuRunQuery($s);\n$o = 10;\n\nwhile($r = db_fetch_object($t)){\n        \n    $s  = \"\n        UPDATE zzzzsys_tab \n        SET syt_order = \'$o\'\n        WHERE zzzzsys_tab_id = \'$r->zzzzsys_tab_id\'\n        ORDER BY syt_order;\n    \";\n    \n    nuRunQuery($s);\n    \n    $o = $o + 10;    \n    \n}\n\nnuCloneForm();', '', '', '1', ''),
('nucalcobjects_BB', 'nucalcobjects_BB', 'System PHP', '', '\n$s = \"\n\nCREATE TABLE #TABLE_ID#\n\nSELECT \n   sob_all_zzzzsys_form_id AS theform,\n   \'\' AS theparent,\n   sob_all_id AS thechild,\n   CONCAT(sob_all_id) as thevalue\nFROM zzzzsys_object \nWHERE sob_input_type = \'number\'\nOR sob_all_type = \'calc\'\n\nUNION \n\nSELECT \n   su.sob_all_zzzzsys_form_id AS theform,\n   su.sob_all_id AS theparent,\n   inp.sob_all_id AS thechild,\n   CONCAT(su.sob_all_id, \'.\', inp.sob_all_id) as thevalue\nFROM zzzzsys_object AS su\nJOIN zzzzsys_object AS inp ON su.sob_subform_zzzzsys_form_id = inp.sob_all_zzzzsys_form_id\nWHERE su.sob_all_type = \'subform\'\nAND (\n        inp.sob_input_type = \'nuNumber\' OR \n        inp.sob_input_type = \'number\' OR \n        inp.sob_all_type = \'calc\'\n    )\n\n\";\n    \nnuRunQuery($s);\n\n', '', '', '1', ''),
('nuform_AD', 'nuform_AD', 'System PHP', '', 'nuDeleteForm(\'#RECORD_ID#\');', '', '', '1', ''),
('nufastform', 'RUNFF', 'Run Fast Form', 'nubuilder', 'nuBuildFastForm(\'#fastform_table#\', \'#fastform_type#\');\n\n', '', 'nufflaunch', '1', ''),
('nuobject_BB', 'nuobject_BB', 'System PHP', '', '$s  = \"CREATE TABLE #TABLE_ID# SELECT zzzzsys_object_id AS theid FROM zzzzsys_object WHERE \";\n$w  = \"1\";\nif ( $GLOBALS[\'nuSetup\']->set_denied == 1 )  { \n$w  = \"sob_all_zzzzsys_form_id NOT LIKE \'nu%\' OR sob_all_zzzzsys_form_id = \'nuuserhome\'\"; \n}\nnuRunQuery(\"$s$w\");\n', '', '', '1', ''),
('nuform_BB', 'nuform_BB', 'System PHP', '', '$s  = \"CREATE TABLE #TABLE_ID# SELECT zzzzsys_form_id AS theid FROM zzzzsys_form WHERE \";\n$w  = \"1\";\nif ( $GLOBALS[\'nuSetup\']->set_denied == 1 )  { \n$w  = \"zzzzsys_form_id NOT LIKE \'nu%\' OR zzzzsys_form_id = \'nuuserhome\'\"; \n}\nnuRunQuery(\"$s$w\");\n', '', '', '1', ''),
('nuphp_BB', 'nuphp_BB', 'System PHP', '', '\n$s  = \"CREATE TABLE #TABLE_ID# SELECT zzzzsys_php_id AS theid FROM zzzzsys_php WHERE \";\n$w  = \"sph_system != \'1\'\";\n\nnuRunQuery(\"$s$w\");\n\n', '', '', '1', ''),
('nuselect_BS', 'nuselect_BS', 'System PHP', '', '$rid    = \'#RECORD_ID#\';\n\nif($rid != \'-1\' and $rid != \'-2\'){ \n    \n    $s      = \"SELECT * FROM zzzzsys_select WHERE zzzzsys_select_id = \'$rid\'\";\n    $t      = nuRunQuery($s);\n    $r      = db_fetch_object($t);\n    \n    if(db_num_rows($t) == 0){\n        \n        $s              = \"\n        INSERT INTO zzzzsys_select\n        (zzzzsys_select_id, sse_system)\n        VALUES\n        (\'$rid\', \'1\')\n        \";\n        \n        nuRunQuery($s);\n        \n    }\n    \n}\n\n', '', '', '1', ''),
('nuphp_BE', 'nuphp_BE', 'System PHP', 'nubuilder', '$rid    = \'#RECORD_ID#\';\n\nif($rid != \'-1\' and $rid != \'-2\'){ \n    \n    $s      = \"SELECT * FROM zzzzsys_php WHERE zzzzsys_php_id = \'$rid\'\";\n    $t      = nuRunQuery($s);\n    $r      = db_fetch_object($t);\n    \n    if(db_num_rows($t) == 0){\n    \n        $s              = \"\n        INSERT INTO zzzzsys_php\n        (\n            zzzzsys_php_id,\n            sph_code,\n            sph_description,\n            sph_group,\n            sph_system\n        )\n        VALUES\n        (\n            \'$rid\', \n            \'$rid\', \n            \'System PHP\', \n            \'nubuilder\', \n            \'1\'\n        )\n        \";\n        \n        nuRunQuery($s);\n        \n    }\n    \n}\n\n', '', '', '1', ''),
('nutranslate_AS', 'nutranslate_AS', 'System PHP', 'nubuilder', '\n$i  = \'trl_english\';\n$t  = nuRunQuery(\'SELECT COUNT(*) FROM zzzzsys_debug\');\n$c  = db_fetch_row($t)[0];\n\n$j  = \";$(\'#$i\').val($c);console.log(\'callback\');\";\n\nnuJavascriptCallback($j);\n\n\n\n', '', '', '1', ''),
